INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (604, N'Corto Maltese', N'Beyond the Windy Isles', N'Corto Maltese; Dr. Stone; Mendoza; Marangwè; Jeremiah Steiner; Levi Columbia', N'Dean Mullaney', N'128', N'May 2015', N'978-1-63140-317-0', N'978163140317052999', N'Dr. Stone decides to go to Peru to find his grandson, who is living with his maternal grandfather Marangwè in a local indigenous tribe after the death of his parents. Stone asks for help from Levi Colombia, Steiner and Corto. Corto agrees, and is accompanied in his search by a local native, who turns out to be Marangwè. The village has been attacked by Mendoza, a slave trader. Corto and Marangwè track the slave trader and rescue the prisoners. Corto takes Marangwè and the grandson back to the mission. Stone decides not to relocate his grandson; Stone and his wife decide to move there instead.', N'', N'IDW', N'117009')
