INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (693, N'Showcase', N'', N'Colonel Henry Casper; Dr. Will Magnus; Metal Men [Platinum [Tina]; Gold; Lead; Iron; Mercury; Tin] (introduction); flying manta ray; Amos J. Talbot', N'Robert Kanigher', N'36', N'March-April 1962', N'', N'', N'In prehistoric times, a flying manta ray survives a "radioactive storm" and is encased in ice.  Today, it breaks free and soars over the coast, zapping lighthouses, jets, and the Chrysler Building with laser-eyes.  The Joint Chiefs send Col. Casper to Dr. Will Magnus''s vast laboratory complex.  Magnus dances with a platinum robot - who talks!  To fight the "creature", Magnus creates five more robots: Gold, Lead, Iron, Mercury, and Tin.  "Meet the Metal Men!"  A UHF TV shows the creature tearing up a cable bridge.  "We''re going to battle it!"', N'', N'DC', N'1194')
