INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (1329, N'Challengers of the Unknown', N'', N'Challengers of the Unknown [Ace Morgan; Rocky Davis; Prof Haley; Red Ryan; June Robbins]; F. Gaylord Clayburne III; Heathcliff Monroe (villain); M''Nagala (villain); cultists (villains)', N'Joe Orlando', N'36', N'August-September 1977', N'', N'0709893045309', N'Prof is dying of an all-consuming fungus infection. Rocky, June, Ace, and Red rush him to their jet. Gaylord Clayburne, playboy-Challenger-wannabe, learned Prof was sick and found a specialist to heal him. Ignoring Clayburne, the Challengers zoom off to Perdition, Pennsylvania, a farm town. As soon as they land, the Challengers are mobbed by farmers with pitchforks. Doctor Monroe calls off the farmers.', N'', N'DC', N'1293')
