INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (1526, N'Shazam!', N'', N'King Kid; Shazam [Billy Batson]; Mary Bromfield; Black Adam; Doctor Sivana; Mr. Mind; Rosa Vasquez; Victor Vasquez; C. C. Batson; Marilyn Batson (flashback); Mr. Tawny (photo); Pedro Peña; Eugene Choi; the Wizard Shazam', N'Işşk Özbay', N'36', N'August 2019', N'', N'76194136016400611', N'Billy and Mary escape from King Kid in the Funlands and suddenly find themselves back home on Earth, where Billy meets his long lost father and Mary must make a decision.  Meanwhile, Sivana and Mr. Mind try to convince Black Adam to join forces with them, and in the Gamelands, Pedro and Eugene try to get a high score so they can return home but they encounter an unexpected ally.', N'', N'DC', N'135089')
