INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2271, N'Star Trek: Year Five', N'', N'Gary Seven [Caleb Howell]; Isis; the Aegis; Kolok Five (Vulcan); Azaram Nineteen (Klingon); unnamed Andorian; unnamed Cardassian; Jimi Hendrix; Genghis Khan; unnamed Vulcans; unnamed seamen; Captain James T. Kirk; Commander Spock; Lt. Montgomery Scott [Scotty]', N'ckoe', N'32', N'November 2020', N'', N'82771401768701711', N'35 year old Caleb Howell is taken from Earth by the Aegis and transformed into a powerful being who is pressed into service as agent Gary Seven who is assigned to protect Earth, with the help of his partner Isis, a woman who can turn into a cat (or vice versa).  After serving as a protector of Earth for many years, Gary Seven learns that for the galaxy to survive, all races except the Tholians must be eliminated, and that the only one who can stop him is Captain Kirk and the Enterprise.', N'', N'IDW', N'144501')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2272, N'Star Trek: Year Five', N'', N'Captain James T. Kirk; Dr. Leonard McCoy [Bones]; Lt. Hikaru Sulu; Ensign Pavel Chekov; Lt. Nyota Uhura; Lt. Arex; Commander Spock; Lt. Montgomery Scott [Scotty]; Nurse Christine Chapel; Isis; Caylx Wairata (Proxima Centaurian); Mar''Pau''ak (Proxima Centaurian); Ensign Richards; Ensign Kellbach; unidentified Enterprise officers; unidentified Proxima Centaurians', N'ckoe', N'32', N'December 2020', N'', N'82771401768701811', N'The Enterprise answers a distress call from Proxima Centauri, where the find the populace of the planet the victims of a widespread plague.  As Kirk, Spock, and McCoy beam down to the planet to investigate, Chekov discovers evidence of familiar readings that may be the source of the plague.', N'', N'IDW', N'144501')
