INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2301, N'Green Lantern', N'', N'Green Lantern [Hal Jordan; as Haljor the Barbarian (in dream)]; Dorine [as Dorinda in dream]; Princess Caarol (in dream); Myrwhydden (villain); Mr. Forbes; Guardians of the Universe; Green Lantern [Abin Sur] (flashback); John Stewart (last page cameo)', N'Ernie Colón', N'36', N'May 1983', N'', N'', N'Myrwhydden who was trapped in Green Lantern''s ring by Abin Sur breaks free. John Stewart is called upon by the Guardians to stop an alien made of crystal.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2302, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Star Sapphire [Carol Ferris]; Sinestro; Thomas Kalmaku [Pieface]; Terga Kalmaku; Keith Kalmaku', N'Julius Schwartz', N'36', N'January 1970', N'', N'', N'Sinestro reveals that he gave Carol Ferris the gem that transformed here into Star Sapphire again.  However, she turns against him when she realizes that he wants to kill Green Lantern.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2303, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Olivia Reynolds; Dr. Eli Bently; Kimon; Qwardians', N'Julius Schwartz', N'36', N'March 1970', N'', N'', N'When Olivia Reynolds is stricken by a mysterious illness, Green Lantern traces it to the antimatter universe of Qward.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2304, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Jubal Slade; Guardians of the Universe; Jeremy Tine; Martin Luther King, Jr.; Robert Kennedy; Old Timer', N'Julius Schwartz', N'36', N'April 1970', N'', N'', N'Green Arrow shows Green Lantern the plight of the people living in the tenements of slumlord Jubal Slade. But when the Guardians of the Universe observe GL roughing up Slade, they bring him to Oa and reprimand him. Against their orders GL returns to Earth and collects evidence to put Slade away. In response, the Guardians contact Green Lantern, but Green Arrow argues that there are serious problems across America that have escaped the attention of Green Lantern. The Guardians confer, and decide to send a representative to accompany the two heroes as they go on a journey of discovery.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2305, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Guardians of the Universe; Old-Timer; Jacob; Slapper Soames; Johnny Walden; Black Canary (cameo, preview panel for following issue)', N'Julius Schwartz', N'36', N'June 1970', N'', N'', N'Green Lantern, Green Arrow, and the Old-Timer come across the company town of Desolation, where the boss Slapper Soames plans to execute Johnny Walden to silence the songs he sings which have inspired the people of Desolation to think about a better way of life. As Green Lantern joins the townspeople in attacking Soames'' stronghold, the Guardians inform him that since he is on a leave of absence they have reduced the power of his ring. But even weakened, Green Lantern and Green Arrow are able to free Johnny and turn Soames and his men over to the authorities.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2306, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Black Canary [Dinah Drake Lance]; Old-Timer; Joshua; Aquarius (cameo, flashback); Larry Lance (cameo, flashback); Superman (cameo, flashback); Flash [Barry Allen] (cameo, flashback); Batman [Bruce Wayne] (cameo, flashback); Superman (of Earth-2, cameo, flashback); Hawkman [Katar Hol] (cameo, flashback); Dr. Fate [Kent Nelson] (cameo, flashback); Dr. Mid-Nite [Charles McNider] (cameo, flashback); Green Lantern [Alan Scott] (cameo, flashback); Wonder Woman (of Earth-2, cameo, flashback)', N'Julius Schwartz', N'36', N'July 1970', N'', N'', N'After a group of thugs overpower her and steal her bike, Black Canary is found by mind-controlling cult leader Joshua. When Green Lantern and Green Arrow encounter the thugs later and recognize Canary''s bike, they set off in search of their friend. Though Canary initially refuses to leave the cult, when Joshua orders her to shoot Green Arrow, her mind won''t let her, and she breaks free from Joshua''s control.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2307, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen; also as the ghost of Ulysses Star]; Old-Timer; Theodore Pudd; Pierre O''Rourke; Abe Star; Black Canary [Dinah Drake Lance]; Congressman Sullivan', N'Julius Schwartz', N'36', N'September 1970', N'', N'', N'Green Lantern and Green Arrow get involved in a dispute over logging rights on Native American land. While GL searches for documentation for the Indians'' claim, GA tries to inspire them with the ghost of their ancestor. Though the heroes are able to prevent all-out conflict between the loggers and the Natives, and the leaders of the loggers are arrested, only time will tell if the Indians'' claim will hold up in a court of law.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2308, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Old-Timer; Guardians of the Universe; Tribune', N'Julius Schwartz', N'36', N'October 1970', N'', N'', N'For putting the welfare of Green Lantern ahead of the ecological welfare of Planet Earth, the Guardians of the Universe send Old-Timer, accompanied by Green Lantern and Green Arrow as witnesses, to the Planet Gallo to be judged by the Tribune. But when they are all muzzled and the Old-Timer is judged guilty without trial, the heroes realize that something is terribly wrong. They discover that the real Tribune have been replaced by their mechanic and fight to restore the proper process of justice and save the life of the Old-Timer.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2309, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Black Canary [Dinah Drake Lance]; Old-Timer; Guardians of the Universe; Mother Juna', N'Julius Schwartz', N'36', N'December 1970', N'', N'', N'Green Lantern and Green Arrow, now joined by Black Canary, travel to Oa and find that Old-Timer has been found guilty by the Tribune. The Guardians strip him of his immortality and sentence him to live out his days on the planet Maltus. The heroes go with him into his exile and discover a planet wildly over-populated due to the efforts of Mother Juna. After they put a stop to her work, the Old-Timer vows to restore balance to Maltus.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2310, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Black Canary [Dinah Drake Lance]; Witch Queen; Sinestro; Medusa', N'Julius Schwartz', N'36', N'February-March 1971', N'', N'', N'When Green Arrow and Black Canary are confronted by harpies, GA calls Green Lantern for help. GL follows the harpies into a trap laid by The Witch Queen on behalf of her brother Sinestro. Following a lead, GA and BC meet a group of Amazons, who, impressed by Black Canary''s fighting skills, explain how in the distant past they were banished to another plane by a wizard, but have been allowed to return by The Witch Queen to make all men pay. With this information, the duo follows the trail to The Witch Queen and rescue Green Lantern from banishment and the grasp of the legendary Medusa.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2311, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Black Canary [Dinah Drake Lance]; Carol Ferris; Sybil; Grandy; Jason Belmore', N'Julius Schwartz', N'36', N'April-May 1971', N'', N'', N'Green Lantern and Green Arrow accompany Dinah as she begins a job at a private school where they are greeted by the cook Grandy, a little girl with psychic powers named Sybil, and the owner Jason Belmore. After Belmore asks the costumed heroes to leave, Dinah is confronted by Grandy, who explains that in Sybil he finally found a way to keep the children of the school in order. Green Lantern and Green Arrow return to the school in time to save Dinah, and when Grandy orders Sybil to attack GL, she refuses and brings down the roof on herself and Grandy.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2312, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Carol Ferris; Black Hand [Wilbur Palm]; Dinah Drake Lance [Black Canary]', N'Julius Schwartz', N'36', N'June-July 1971', N'', N'', N'While Carol Ferris is in Piper''s Dell, the sea wall protecting the town begins to give way, so Hal responds as Green Lantern. After saving the town, GL is given a tour of the plastic factory. Weakened by gas emitted by the plastic pins produced there, he is unable to fight back, so he sends his ring to Green Arrow. When he revives, he finds that his old enemy Black Hand uses the pins to keep the community under his control. He sends GL and Carol out into the streets to face the wrath of the townspeople, but Green Arrow arrives in time with GL’s ring, and GL traps Black Hand in his own plastic.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2313, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Roy Harper [Speedy]; Mr. Browden', N'Julius Schwartz', N'52', N'August-September 1971', N'', N'', N'When Oliver is jumped by junkies, he is shocked when one of them shoots him with one of his own arrows. Green Arrow, now teamed with Green Lantern, follows the leads to find the junkies, including the missing Roy Harper, who lets GA assume he has been working undercover. Later, the junkies lead the heroes to an airplane hangar, but help the dealers knock out GL and GA and inject them with drugs. Roy arrives in time to get the messed up duo home. There he tries to explain the attraction of doing drugs, but Green Arrow remains baffled, and is shocked to soon find Roy getting ready to shoot up.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2314, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Roy Harper [Speedy]; Dinah Drake Lance [Black Canary]; Salomon Hooper', N'Julius Schwartz', N'52', N'October-November 1971', N'', N'', N'Having discovered that Roy is a junkie, Green Arrow sets out to bring the drug traffickers down. Green Lantern returns to Oliver''s building and finds Roy curled up in the basement. He takes Roy to Dinah''s to straighten out and heads out to find Green Arrow, who has been knocked out by the drug dealers and tossed into the harbor to drown. GA escapes just as GL arrives to take out the goons who threw him into the water. Together the two trace the drugs to pharmaceutical magnate Salomon Hooper and put him out of business. But Roy explains that drugs are not the root problem, just a symptom of it.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2315, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Guy Gardner; Guardians of the Universe; Green Lantern [John Stewart] (intro); Jeremiah Clutcher', N'Julius Schwartz', N'52', N'December 1971-January 1972', N'', N'', N'When Guy Gardner is injured, the Guardians of the Universe instruct Hal Jordan to train another back-up, but John Stewart has different ideas about how to be a Lantern. When an attempt is seemingly made on the life of racist politician Jeremiah Clutcher, John Stewart figures out that the attack was faked and part of a cynical plot to get Clutcher elected president.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2316, N'Green Lantern', N'', N'Green Lantern [Hal Jordan] (brief origin); Carol Ferris; A Venusian Pterodactyl; Venusians', N'Julius Schwartz', N'52', N'February-March 1972', N'', N'', N'Green Lantern travels to Venus to save a blue-skinned caveman-like race from attack by yellow Pterodactyl-like creatures.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2317, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Isaac; Barbie Dole; Barnaby Catts; Carol Ferris; Mr. Tyrone', N'Julius Schwartz', N'52', N'April-May 1972', N'', N'', N'Green Arrow goes with Green Lantern and Carol Ferris to the Ferris Aircraft plant in Abraham, which has been the target of ecological prankster Isaac. When Isaac accidentally threatens Carol, GL tries to take him into custody, but GA gases him. Offended at this abuse of the atmosphere, Isaac turns his back on Green Arrow. Ferris security subdues GA and gathers the passed out GL.  Finding Isaac has lashed himself to an aircraft engine, Ferris security straps GL and GA to the tail wings of two planes and leaves Isaac where he is to die. GA frees himself, but not in time to save Isaac.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2318, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Guardians of the Universe; unnamed Green Lantern (a Vulcan); other Green Lanterns (unnamed); Jinn (alien criminal); unnamed blue-skinned aliens (predecessors of the Green Lantern Corps)', N'Denny O''Neil', N'36', N'August-September 1976', N'', N'0709893051509', N'A nuclear test in a Nevada desert uncovers a long-buried alien ship, and when the aliens are revived, Green Lantern and Green Arrow intervene to free an alien captive from his captors.  But all is not as it seems.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2319, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Sinestro; Guardians of the Universe; unnamed alien Green Lantern; Yolanda; Abraxis', N'Denny O''Neil', N'36', N'October-November 1976', N'', N'0709893051511', N'Green Arrow is invited to participate in an archery competition in a small country, unaware that it is a trap set by Sinestro to kill him as revenge against Green Lantern.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2320, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Sinestro; Marion', N'Denny O''Neil', N'36', N'December 1976-January 1977', N'', N'0709893051501', N'Sinestro flees Green Lantern and Green Arrow thru a mysterious object in space called the Silver Twist.  They are transported to a world where the three of them have to work to free the populace from a tyrant.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2321, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]', N'Julius Schwartz', N'36', N'February-March 1977', N'', N'0709893051503', N'Green Lantern is attacked by yellow beams from a spaceship that is beaming up people from Earth.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2322, N'Green Lantern', N'', N'Green Lantern [John Stewart]; Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Itty; Carol Ferris; Colonel Thaddeus Lucius Krisp; Black Canary [Dinah Lance]; Pieface [Thomas Kalmaku]', N'Julius Schwartz', N'36', N'April-May 1977', N'', N'0709893051505', N'Hal Jordan returns to Earth and falls deathly ill.  John Stewart comes out of retirement to take him to Oa for treatment.  Meanwhile Green Arrow and Black Canary infiltrate a government agency.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2323, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]', N'Julius Schwartz', N'36', N'June-July 1977', N'', N'0709893051507', N'Green Arrow has just shot the President of the United States.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2324, N'Green Lantern', N'', N'Green Lantern [Katma Tui]; Green Arrow [Oliver Queen]; Black Canary [Dinah Lance]; Green Lantern [Hal Jordan]; Itty; Green Lanterns (unnamed); Guardians of the Universe (unnamed); Carol Ferris', N'Julius Schwartz', N'36', N'August-September 1977', N'', N'0709893051509', N'An injured Katma Tui arrives on Earth and is attacked by an alien Green Lantern.  While Katma recovers from her injuries, Hal travels to Oa to find out what is going on, but when arrives, he is attacked by the rest of the Green Lantern Corps.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2325, N'Green Lantern', N'', N'The Mocker (1st appearance); Green Lantern [Hal Jordan]; Guardians of the Universe (unnamed); Green Lanterns (unnamed); Itty; Green Arrow [Oliver Queen]; Green Lantern [Katma Tui]; Black Canary [Dinah Lance]; Carol Ferris; Junior Musto (terrorist)', N'Julius Schwartz', N'36', N'October 1977', N'', N'0709893051510', N'On Oa, the Green Lantern Corps and the Guardians have been freed from being controlled mentally, but now must determine who was behind it.  Meanwhile on Earth, Green Arrow and Black Canary battle terrorists who attempt to kidnap Carol Ferris, but during the battle Katma Tui disappears.', N'', N'DC', N'1448')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2326, N'Green Lantern', N'', N'Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Black Canary [Dinah Drake Lance]; Ffa''rzz', N'Julius Schwartz', N'36', N'November 1977', N'', N'0709893051511', N'Ffa''rzz projects a vision of Dinah''s late husband, Larry Lance.', N'', N'DC', N'1448')
