INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2347, N'Justice League of America', N'', N'Justice League of America [Green Arrow [Oliver Queen]; Batman [Bruce Wayne]; Elongated Man [Ralph Dibny]; Aquaman [Arthur Curry]; The Flash [Barry Allen]]; Golden Eagle [Charley Parker] (introduction); Matter Master [Mark Mandrill] (villain); Commissioner George Emmett; Hawkman [Katar Hol] (cameo)', N'Julius Schwartz', N'100', N'March-April 1975', N'', N'', N'Green Arrow investigates a letter set to the JLA and meets Charley Parker, who is following in Hawkman''s crime-fighting footsteps as Golden Eagle. When the Matter Master challenges Golden Eagle, thinking that he''s Hawkman, The Justice League steps in to help. After the villain is defeated, the real Hawkman makes a surprise appearance.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2348, N'Justice League of America', N'', N'Justice League of America [Green Lantern [Hal Jordan]; Batman [Bruce Wayne]; Green Arrow [Oliver Queen]; Superman [Clark Kent; Kal-El]; The Atom [Ray Palmer]; Hawkman [Katar Hol] (rejoins); Red Tornado [John Smith] (cameo); The Flash [Barry Allen] (cameo); Elongated Man [Ralph Dibny] (cameo); Aquaman (cameo); Black Canary [Dinah Laurel Lance] (cameo)]; Golden Eagle [Charlie Parker]; The Equalizer (villain)', N'Julius Schwartz', N'36', N'April 1975', N'', N'', N'The JLA helps Hawkman against the Equalizer, who has unleashed a plague on the people of Thanagar to make them all equal in strength and intellect. Hawkman is voted back into the team as an active member.', N'', N'DC', N'1449')
