INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2350, N'Justice League of America', N'', N'Justice League of America [Aquaman; Black Canary [Dinah Laurel Lance]; The Flash [Barry Allen]; Hawkman [Katar Hol]; Red Tornado [John Smith]; Superman [Clark Kent; Kal-El]; Atom [Ray Palmer]; Batman [Bruce Wayne]; Elongated Man [Ralph Dibny]; Green Arrow [Oliver Queen]; Green Lantern [Hal Jordan]]; Adaptoids (villains); Hawkgirl [Shayera Thal]', N'Julius Schwartz', N'36', N'June 1975', N'', N'', N'The Adaptoids are finally defeated when Hawkman brings Hawkgirl back from Thanagar, exposing the aliens to the Equalizer Plague.', N'', N'DC', N'1449')
