INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2352, N'Justice League of America', N'', N'Justice League of America [Aquaman; Atom [Ray Palmer]; Batman [Bruce Wayne]; Green Arrow [Oliver Queen]; Hawkman [Katar Hol]; Red Tornado [John Smith]; Superman [Clark Kent; Kal-El] (cameo); The Flash [Barry Allen] (cameo); Green Lantern [Hal Jordan] (cameo); Elongated Man [Ralph Dibny] (cameo); Wonder Woman [Diana Prince] (cameo); Black Canary [Dinah Laurel Lance] (cameo)]; Adam Strange (guest star); Kanjar Ro (villain); Alanna; Sardath; Hawkgirl [Shayera Thal] (cameo)', N'Julius Schwartz', N'36', N'August 1975', N'', N'', N'After the Justice League helps Adam Strange defeat Kanjar Ro, Adam marries his long-time sweetheart, Alanna.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2353, N'Justice League of America', N'', N'Justice League of America [Aquaman; The Atom [Ray Palmer]; Batman [Bruce Wayne]; The Flash [Barry Allen]; Green Arrow [Oliver Queen]; Green Lantern [Hal Jordan]; Superman [Clark Kent; Kal-El]]; Dr. Light [Dr. Arthur Light] (villain); Professor Ira West', N'Julius Schwartz', N'36', N'September 1975', N'', N'', N'In this previously untold story, Doctor Light makes the Leaguers forget their own real identities and assume one belonging to another hero. After the villain is defeated, the heroes resolve to share their secret identities from now on.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2354, N'Justice League of America', N'', N'Justice League of America [Aquaman; Batman [Bruce Wayne]; Hawkman [Katar Hol; Carter Hall]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]; The Flash [Barry Allen]]; Justice Society of America [Robin [Dick Grayson]; Johnny Thunder; Thunderbolt [Cei-U]; Hourman [Rex Tyler]; Wonder Woman [Diana Prince Trevor]; Doctor Mid-Nite [Charles McNider]; Wildcat [Ted Grant]]; Injustice Society of the World [The Icicle [Joar Mahkent]; The Sportsmaster [Crusher Crock]; The Wizard; The Gambler [Steven Sharpe III]; The Huntress [Paula Brooks]; The Shade [Richard Swift]] (villains); DC Comics Staff [Cary Bates; Julius Schwartz; Elliot S! Maggin; Carmine Infantino]', N'Julius Schwartz', N'36', N'October 1975', N'', N'', N'On Earth-Prime, Cary Bates and Elliott S! Maggin are contemplating the next issue of JLA to write when they both go into Infantino''s office, stumble upon the Cosmic Treadmill Earth-1''s Flash left behind. Accidentally, they activate it, and Bates disappears to Earth-2 as a villain, with superpowers. Maggin follows, but lands up on E-1 with the JLA. The JLA then goes to Earth-2 to help out the JSA, who has been fighting the Injustice Society, and ''kill'' each member, revealing the members as disguised JSAers. The JLA then vow to stop the Injustice Society and bring them to justice for the JSA.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2355, N'Justice League of America', N'', N'Justice League of America [The Flash [Barry Allen]; Aquaman; Hawkman [Katar Hol; Carter Hall]; Batman [Bruce Wayne]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]]; Justice Society of America [Wonder Woman [Diana Prince Trevor]; Doctor Mid-Nite [Charles McNider]; Robin [Dick Grayson]; Hourman [Rex Tyler]; Wildcat [Ted Grant]; Johnny Thunder; Thunderbolt [Cei-U]; The Spectre [Jim Corrigan]]; Injustice Society of the World [The Wizard; The Huntress [Paula Brooks]; The Shade [Richard Swift]; The Gambler [Steven Sharpe III]; The Sportsmaster [Crusher Crock]; The Icicle [Joar Mahkent]] (villains); God; DC Comics Staff [Elliot S! Maggin; Cary Bates (brainwashed as villain); Julius Schwartz (cameo)] (all of Earth-Prime)', N'Julius Schwartz', N'36', N'November 1975', N'', N'', N'As the JLA battle Cary Bates (as a villain brainwashed by the Wizard) and the Injustice Society (ISW), they are defeated due to the subconscious memories of the JLA defeating the JSA earlier (disguised as the ISW). The Spectre saves the day, when he asks God to restore the JSA using his ring of life, as well as Maggin egging Bates on to defeat verbally. The ISW is then defeated by the JLA/JSA, and Bates and Maggin are returned to Earth-Prime, with no memories of what happened (The Spectre wiping their memories clean), to solve: JLA''s next story to be penned while in Julius Schwartz''s office.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2356, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Green Lantern [Hal Jordan]; Aquaman; The Flash [Barry Allen]; Hawkman [Katar Hol]]; Two-Face [Harvey Dent] (guest star); Dronndarians (villains); Weaponers of Qward (villains) (cameo)', N'Julius Schwartz', N'36', N'December 1975', N'', N'', N'Two-Face is the League''s unlikely ally when a flip of his coin determines that he will help them fight an alien invasion.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2357, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Aquaman; The Flash [Barry Allen]; Hawkman [Katar Hol]; The Atom [Ray Palmer]; Green Lantern [Hal Jordan]]; Two-Face [Harvey Dent] (villain); Weaponers of Qward (villains); Kiman (villain); Joker (cameo)', N'Julius Schwartz', N'36', N'January 1976', N'', N'', N'Two-Face changes sides when his coin comes up on the scarred side, leading his former JLA allies into traps set by the Weaponers of Qward.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2358, N'Justice League of America', N'', N'Justice League of America [Superman; Batman; The Flash [Barry Allen]; Green Lantern [Hal Jordan]; Black Canary [Dinah Lance]; Green Arrow]', N'Julius Schwartz', N'36', N'February 1976', N'', N'', N'Superman punches out Green Lantern.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2359, N'Justice League of America', N'', N'Justice League of America [The Atom [Ray Palmer]; Batman [Bruce Wayne]; Black Canary [Dinah Laurel Lance]; Elongated Man [Ralph Dibny]; Green Arrow [Oliver Queen]; Hawkman [Katar Hol]; Superman [Clark Kent; Kal-El]; Green Lantern [Hal Jordan]; Wonder Woman [Diana Prince]]; Lex Luthor (flashback); Gentleman Ghost (cameo flashback); Guardians of the Universe; Nekron (villain)', N'Julius Schwartz', N'36', N'March 1976', N'', N'', N'Wonder Woman returns to the Justice League but is mystified when she finds that they no longer want to fight crime or put themselves in danger.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2360, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Batman [Bruce Wayne]; Wonder Woman [Diana Prince]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]; Elongated Man [Ralph Dibny]; Hawkman [Katar Hol]; The Flash [Barry Allen]; Aquaman; Red Tornado [John Smith] (dies); Green Lantern [Hal Jordan]; The Atom [Ray Palmer]]; Nekron (villain); Captain Cold [Len Snart] (flashback); Guardians of the Universe; Lex Luthor (flashback)', N'Julius Schwartz', N'36', N'April 1976', N'', N'', N'Wonder Woman uses her Magic Lasso to empower her teammates to overcome the strange mind-control of Nekron, but it''s the Red Tornado who makes the ultimate sacrifice to save them all.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2361, N'Justice League of America', N'', N'Justice League of America [Aquaman; The Atom [Ray Palmer]; Black Canary [Dinah Laurel Lance]; The Flash [Barry Allen]; Green Arrow [Oliver Queen]; Green Lantern [Hal Jordan]; Hawkman [Katar Hol]; Superman [Clark Kent; Kal-El]; Batman [Bruce Wayne] (flashback)]; Dharlu (villain)', N'Julius Schwartz', N'36', N'May 1976', N'', N'0709893055505', N'A malfunction in the League''s new teleportation device merges Flash, Hawkman, and an alien called the Dharlu.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2362, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; The Flash [Barry Allen]; The Atom [Ray Palmer]; Elongated Man [Ralph Dibny]; Aquaman; Hawkman [Katar Hol]]; Queen Bee [Zazzala]; Sonar [Professor Dunkirk] (villain)', N'Julius Schwartz', N'36', N'June 1976', N'', N'0709893055506', N'The League tries to control plagues and other natural disasters that turn out to be part of a larger plot against them.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2363, N'Justice League of America', N'', N'Justice League of America [Batman [Bruce Wayne]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]; Green Lantern [Hal Jordan]; Wonder Woman [Diana Prince]; Elongated Man [Ralph Dibny] (flashback, cameo); The Atom [Ray Palmer] (flashback, cameo); Hawkman [Katar Hol] (cameo); The Flash [Barry Allen] (flashback, cameo); Superman [Clark Kent; Kal-El] (flashback)]; Supergirl [Linda Danvers; Kara Zor-El] (guest star); Queen Bee (villain); Sonar [Professor Dunkirk] (villain)', N'Julius Schwartz', N'36', N'July 1976', N'', N'0709893055507', N'Several Leaguers are missing, including Superman, and Supergirl arrives to help the rest of the team find the others. Sonar''s plan continues to unfold, but it turns out that it''s really the Queen Bee behind it all.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2364, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Flash [Barry Allen]; Green Lantern [Hal Jordan]; Wonder Woman [Diana Prince]; Aquaman; Green Arrow [Oliver Queen] (cameo); Black Canary [Dinah Laurel Lance] (cameo); Elongated Man [Ralph Dibny] (cameo); Atom [Ray Palmer] (cameo); Hawkman [Katar Hol] (cameo)]; Supergirl [Linda Danvers; Kara Zor-El] (cameo); Despero (villain); Director Eragon; Governor Kwim', N'Julius Schwartz', N'36', N'August 1976', N'', N'0709893055508', N'Superman has been transported to the planet Sirkus by a filmmaker who convinces him he is fighting beside his fellow Leaguers (actually robot stand-ins for the filming) against Despero, who is attacking the planet.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2365, N'Justice League of America', N'', N'Justice League of America [Batman [Bruce Wayne]; Wonder Woman [Diana Prince]; Atom [Ray Palmer]; Elongated Man [Ralph Dibny]; Hawkman [Katar Hol]; Superman [Clark Kent; Kal-El]; Black Canary [Dinah Laurel Lance]; Green Lantern [Hal Jordan] (cameo); Aquaman (cameo)]; Supergirl [Linda Danvers; Kara Zor-El] (guest star); Despero (villain); Albon; Nordon', N'Julius Schwartz', N'36', N'September 1976', N'', N'0709893055509', N'Two aliens teleport teams of Leaguers in and out of battle with Despero for their own inexplicable purposes.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2372, N'Justice League of America', N'', N'Justice League of American [JLA; Superman [Clark Kent; Kal-El]; Batman [Bruce Wayne]; Wonder Woman [Diana Prince]; The Flash [Barry Allen]; Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]]; Manhunters (villains); The Grandmaster (villain); Manhunter [Mark Shaw]; Guardians of the Universe; Dharlu', N'Joe Orlando', N'52', N'April 1977', N'', N'0709893055504', N'The Guardians reveal the secret origin of the Manhunters, as renegades who originally worked for the Guardians. Mark Shaw discovers that the Grandmaster has betrayed the ideals of the Manhunters and he helps the League defeat the Grandmaster''s plot.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2366, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Flash [Barry Allen]; Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]; Hawkman [Katar Hol; Carter Hall]; Hawkwoman [Shayera Hol; Shiera Hall]] (all Earth-1); Justice Society of America [Batman [Gotham City Police Commissioner Bruce Wayne]; Robin [Dick Grayson]; Wonder Woman [Diana Prince Trevor]; Flash [Jay Garrick]; Green Lantern [Alan Scott]; Johnny Thunder; Thunderbolt] (all Earth-2); Shazam''s Squadron of Justice [Ibis; Spy Smasher [Alan Armstrong]; Bulletgirl [Susan Kent]; Bulletman; Mr. Scarlet [Brian Butler]; Pinky]; Taia; King Kull (Earth-S); Queen Clea (Earth-2 villain); Blockbuster [Mark Desmond] (Earth-1 villain); The Penguin (Earth-1 villain); IBAC (Earth-S villain); Shazam (wizard); Pantheon of Gods and Goddesses [Solomon; Hercules; Atlas; Zeus; Achilles; Mercury; Selena; Hyppolyta; Athena; Zephyrus; Aphrodite; Minerva] (all Earth-S)', N'Julius Schwartz', N'36', N'October 1976', N'', N'0709893055510', N'King Kull begins his newest conquest plan on Earth-S as he freezes both the wizard Shazam and the Pantheon of Gods and Goddesses on the Rock of Eternity. Only Mercury was able to escape, and the wizard sends him to both Earths 1 and 2 to get the JLA and JSA to aid them. The villainous group of Earth-S''s IBAC,Earth-2''s Clea and Earth-1''s Blockbuster and Penguin are defeated by both teams, as well as the Squadron of Justice (SoJ), but that is only part of Kull''s full plan defeated. To be continued...', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2367, N'Justice League of America', N'', N'Justice League of America [Hawkman [Katar Hol; Carter Hall]; Hawkwoman [Shayera Thal; Shiera Hall]] (all Earth-1); Justice Society of America [Batman [Gotham City Police Commissioner Bruce Wayne]; Robin [Dick Grayson]; Johnny Thunder; Thunderbolt] (all Earth-2); Shazam''s Squadron of Justice [Mister Scarlet; Pinky; Bulletman; Bulletgirl] (all Earth-S); King Kull (Earth-S villain); The Shade [Richard Swift] (Earth-2 villain); The Joker (Earth-2 villain, intro); The Weeper (Earth-S villain); Doctor Light [Arthur Light] (Earth-1 villain); Marvel Family [as powerless Billy Batson; Freddy Freeman; Mary Batson]', N'Julius Schwartz', N'36', N'November 1976', N'', N'0709893055511', N'As King Kull''s plan continues, the JLA,JSA and Squadron of Justice teams thwart the next phase. Johnny Thunder and his Thunderbolt (with Mercury), meanwhile, meets up with a powerless Billy and Mary Batson, and Freddy Freeman, who have been wondering with all that has been occuring why they haven''t been able to summon Shazam''s mystic thunderbolts to change them into the Marvel Family. Johnny''s Thunderbolt fixes this by using his magic to change them, and now the Marvels ready themselves to join in the fight. To be concluded...', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2373, N'Justice League of America', N'', N'Justice League of America [JLA; Aquaman; The Atom [Ray Palmer]; Elongated Man [Ralph Dibny]; Batman [Bruce Wayne]; Black Canary [Dinah Laurel Lance]; The Flash [Barry Allen]; Green Arrow [Oliver Queen]; Green Lantern [Hal Jordan]; Superman [Clark Kent; Kal-El]; Wonder Woman [Diana Prince]]; The Construct (introduction, villain); Willow; King Vulko; Manhunters (flashback)', N'Joe Orlando', N'52', N'May 1977', N'', N'0709893055505', N'While the rest of the League battled the Manhunters, Aquaman, Atom, and Elongated Man were fighting The Construct, an electronic energy composite being who is trying to destroy a woman from another reality.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2368, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Green Arrow [Oliver Queen]; The Flash [Barry Allen]; Green Lantern [Hal Jordan]; Hawkman [Katar Hol; Carter Hall]; Hawkwoman [Shayera Thal; Shiera Hall]] (all Earth-1); Justice Society of America [Batman [Gotham City Police Commissioner Bruce Wayne]; Robin [Dick Grayson]; Johnny Thunder; Green Lantern [Alan Scott]; The Flash [Jay Garrick]; Thunderbolt; Wonder Woman [Diana Prince Trevor]] (all Earth-2); Marvel Family [Captain Marvel [Billy Batson]; Mary Marvel [Mary Batson]; Captain Marvel, Jr. [Freddy Freeman]] (all Earth-S); Shazam; King Kull (Earth-S villain); Mr. Atom (Earth-S villain); Brainiac (Earth-1 villain); Shazam''s Squadron of Justice [Bulletman; Bulletgirl; Pinky; Mr. Scarlet; Ibis] (all Earth-S); Pantheon of Gods and Goddesses [Solomon; Hercules; Atlas; Zeus; Achiles; Mercury; Selena; Hyppolyta; Artemis; Zephyrus; Aphrodite; Minerva] (all Earth-S)', N'Julius Schwartz', N'36', N'December 1976', N'', N'0709893055512', N'As the final part of King Kull''s plan continues, it is thwarted once again by the Justice League, the Justice Society, and the Squadron of Justice. When Superman confronts Kull on the Rock of Eternity however, he''s turned into a killer by a dose of Red Kryptonite and almost destroys Captain Marvel, but when Marvel utters “Shazam”, the spell is broken and is saved by Superman while in freefall as Billy. Kull is then captured by the Marvel Family and the four triumphant heroic groups then leave back to their respective worlds from the Rock.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2369, N'Justice League of America', N'', N'Justice League of America [JLA; Superman [Clark Kent; Kal-El]; Batman [Bruce Wayne]; Wonder Woman [Diana Prince]; The Flash [Barry Allen]; Aquaman; Green Lantern (of 73rd century)]; Adam Strange (guest star); Alanna; Sardath (flashback); Iris Allen (flashback)', N'Julius Schwartz', N'36', N'January 1977', N'', N'0709893055501', N'Alanna enlists the League to help her husband Adam, who has been drawn to the 73rd century multiple times by a freak Zeta-Beam, but the heroes learn that the situation is even worse than they thought.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2370, N'Justice League of America', N'', N'Justice League of America [JLA; Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Hawkman [Katar Hol]; Black Canary [Dinah Laurel Lance]; Aquaman; Superman [Clark Kent; Kal-El]; The Flash [Barry Allen]; Batman [Bruce Wayne]; Wonder Woman [Diana Prince]]; Adam Strange (guest star); Alanna; Kanjar Ro [also as Green Lantern] (of the 73rd century, villain)', N'Julius Schwartz', N'52', N'February 1977', N'', N'0709893055502', N'Adam Strange enlists the help of the remaining Leaguers to restore their comrades, who have been rendered invisible by Kanjar Ro.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2371, N'Justice League of America', N'', N'Justice League of America [JLA; Superman [Clark Kent; Kal-El]; Batman [Bruce Wayne]; Wonder Woman [Diana Prince]; Flash [Barry Allen]; Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]]; Manhunters (villains); Manhunter [Mark Shaw]; The Grandmaster (villain); Guardians of the Universe', N'Julius Schwartz', N'52', N'March 1977', N'', N'0709893055503', N'Manhunter Mark Shaw captures Green Lantern, Green Arrow and Black Canary, accusing GL of destroying an inhabited planet. Batman proves that the accusation is false, but the League uncovers a much larger plot by the Manhunters to discredit the Guardians of the Universe.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2374, N'Justice League of America', N'', N'Justice League of America [JLA; Superman [Clark Kent; Kal-El]; Wonder Woman [Diana Prince]; Aquaman [Arthur Curry]; Atom [Ray Palmer]; Batman [Bruce Wayne]; Black Canary [Dinah Laurel Lance]; Elongated Man [Ralph Dibny]; The Flash [Barry Allen]; Green Arrow [Oliver Queen]; Green Lantern [Hal Jordan]; Hawkman [Katar Hol]]; Injustice Gang of the World [Scarecrow; Poison Ivy [Pamela Isley]; Mirror Master [Sam Scudder]; Chronos [David Clinton]; Tattooed Man [Abel Tarrant]]; Hawkgirl [Shayera Hol]; Privateer [Mark Shaw] (formerly Manhunter); Construct-II', N'Joe Orlando', N'52', N'June 1977', N'', N'0709893055506', N'The Construct reforms itself and controls the minds of several Leaguers, causing them to fight each other and several of them to resign from the team. It then gathers the Injustice Gang members to fight the weakened JLA.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2375, N'Justice League of America', N'', N'Justice League of America [Green Arrow [Oliver Queen]; Green Lantern [Hal Jordan] (also in flashback as Hal Jordan); Superman [Clark Kent; Kal-El] (also in flashback); Martian Manhunter [J''onn J''onzz] (on tape and in flashback); The Flash [Barry Allen] (flashback); Batman [Bruce Wayne] (flashback); Aquaman (flashback); Wonder Woman [Diana Prince] (flashback)]; Robin [Dick Grayson] (flashback); Challengers of the Unknown [Ace Morgan; Prof. Haley; Red Ryan; Rocky Davis] (flashback); the Blackhawks [Blackhawk; Andre; Stanislaus; Hendrickson; Chop-Chop; Olaf; Chuck] (flashback); Dr. Saul Erdel (flashback); Commander Blanx (villain) (flashback); Roy Raymond (flashback); Karen Duncan (flashback) Lois Lane (flashback); Jimmy Olsen (flashback); Rex the Wonder Dog (flashback); Congo Bill (flashback); Congorilla (flashback); Robotman [Dr. Robert Crane] (flashback); The Vigilante [Greg Sanders] (flashback); Plastic Man (flashback); Rip Hunter (flashback); Jeff Smith (flashback); Adam Strange (flashback); Alanna (flashback)', N'Joe Orlando', N'52', N'July 1977', N'', N'0709893055507', N'Featuring the near-origin of the JLA told by the Martian Manhunter via tape to Green Arrow.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2376, N'Justice League of America', N'', N'Justice League of America [JLA; Batman [Bruce Wayne]; Black Canary [Dinah Laurel Lance]; Green Arrow [Oliver Queen]; Hawkman [Katar Hol]; Phantom Stranger; Superman [Clark Kent; Kal-El]; Wonder Woman [Diana Prince]; Red Tornado [John Smith] (cameo)]; Hawkgirl [Shayera Thal]; Count Crystal (villain); Azgore (villain); Tom Fagan', N'Joe Orlando', N'52', N'August 1977', N'', N'0709893055508', N'At his Carnival of Souls outside Rutland, Vermont, Count Crystal attempts to sacrifice the Justice League to the demon Azgore to attain ultimate power. He manages to kill Superman, Phantom Stranger, and Hawkman but they are revived when he is defeated. The Red Tornado also reappears at the end of the story.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2377, N'Justice League of America', N'', N'Justice League of America [JLA; Aquaman; Atom [Ray Palmer]; Batman [Bruce Wayne]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]; Hawkman [Katar Hol]; Phantom Stranger; Superman [Clark Kent; Kal-El]; Wonder Woman [Diana Prince]; Hawkgirl [Shayera Hol] (joins); Red Tornado [John Smith] (rejoins)]; Willow (flashback); The Construct (villain); Hippolyta', N'Joe Orlando', N'52', N'September 1977', N'', N'0709893055509', N'Red Tornado must prove himself again to the League as they battle the newest version of the Construct. Hawkgirl also convinces the others to vote her in as a member.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2378, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Batman [Bruce Wayne]; The Flash [Barry Allen]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]; Green Lantern [Hal Jordan]]; Justice Society of America [The Flash [Jay Garrick]; Wildcat [Ted Grant]; Star-Spangled Kid [Sylvester Pemberton]; Green Lantern [Alan Scott]; Power Girl [Kara Zor-L]; Dr. Fate [Kent Nelson]; Hawkman [Carter Hall]]; Legion of Super-Heroes [Lightning Lad [Garth Ranzz]; Shadow Lass [Tasmia Mallor]; Chameleon Boy [Reep Daggle]; Saturn Girl [Imra Ardeen]; Ultra Boy [Jo Nah]; Sun Boy [Dirk Morgna]; Brainiac 5 [Querl Dox]; Wildfire [Drake Burroughs]; Princess Projectra]; The Demons Three [Abnegazar; Rath; Ghast] (villains); Psycho-Pirate [Roger Hayden] (cameo); Mordru (villain)', N'Joe Orlando', N'52', N'October 1977', N'', N'0709893055510', N'The JLA and JSA, alongside the Legion are captured by Mordru in the 30th century and forced to do his task- To reclaim the three items lost after the JLA satellite was destroyed in the late 20th century: Bell, Wheel, and Jar, so the three demons the JLA have fought before on Earth-1: Abnegazar, Rath, and Ghast, are restored, so they can do Mordru''s biddings.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2379, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Batman [Bruce Wayne]; Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]]; Justice Society of America [The Flash [Jay Garrick]; Green Lantern [Alan Scott]; Hawkman [Carter Hall]; Power Girl [Kara Zor-L]; Dr. Fate [Kent Nelson]]; Legion of Super-Heroes [Chameleon Boy [Reep Daggle]; Ultra Boy [Jo Nah]; Princess Projectra; Saturn Girl [Imra Ardeen]; Sun Boy [Dirk Morgana]; Brainiac 5 [Querl Dox]; Wildfire [Drake Burroughs]; Lightning Lad [Garth Ranzz]; Shadow Lass [Tasmia Mallor]]; The Demons Three [Abnegazar; Rath; Ghast] (villains); Mordru (villain)', N'Joe Orlando', N'52', N'November 1977', N'', N'0709893055511', N'As all three teams recover the three items, and the three demons are restored, changes have happened between the demons while imprisoned over a 1000 yrs: Abnegazar wants to be peaceful, while Rath wants to rule everything, and Ghast just wants to restore the entire Earth-1 universe back to how it was before they were first captured. Eventually, the JLA/JSA/Legion teams defeat all three demons and Mordru, and go their separate ways.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2380, N'Justice League of America', N'', N'Justice League of America [JLA; Batman [Bruce Wayne]; Hawkgirl [Shayera Hol]; Red Tornado [John Smith]; Superman [Clark Kent; Kal-El]; The Flash [Barry Allen]; Hawkman [Katar Hol]; Green Lantern [Hal Jordan]; Wonder Woman [Diana Prince]; The Atom [Ray Palmer] (flashback); Martian Manhunter [J''onn J''onzz] (flashback); Green Arrow [Oliver Queen] (flashback)]; Dr. Light (villain); The Privateer [Mark Shaw; formerly Manhunter]; Snapper Carr [Star-Tsar]; Joker (flashback)', N'Joe Orlando', N'52', N'December 1977', N'', N'0709893055512', N'Mark Shaw, now calling himself the Privateer, alerts the League to the return of Doctor Light, but while they are pursuing the villain a new enemy appears: the Star-Tsar (revealed to be Snapper Carr).', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2381, N'Justice League of America', N'', N'Justice League of America [JLA; Superman [Clark Kent; Kal-El]; Batman [Bruce Wayne]; Wonder Woman [Diana Prince]; Green Lantern [Hal Jordan]; The Flash [Barry Allen]; Red Tornado [John Smith]; Hawkman [Katar Hol]; Hawkwoman [Shayera Thal; also as Hawkgirl]; Green Arrow [Oliver Queen]; Black Canary [Dinah Laurel Lance]; Elongated Man [Ralph Dibny]; Aquaman; The Atom [Ray Palmer]; Phantom Stranger]; Star-Tsar [Snapper Carr]; Dr. Light (flashback); The Privateer [Mark Shaw; also as Star-Tsar] (villain); The Key (villain); Janet Carr; Anakronus (flashback); President Jimmy Carter', N'Joe Orlando', N'52', N'January 1978', N'', N'0709893055501', N'When the League tries to figure out why Snapper Carr has turned against them again they discover that the Key has returned from the dead and gave Snapper his new powers. However, the Privateer is revealed as the true Star-Tsar by Red Tornado.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2382, N'Justice League of America', N'', N'Justice League of America [Superman [Kal-El]; Hawkman [Katar Hol]; Elongated Man; Flash [Barry Allen]; Green Arrow [Oliver Queen]; Black Canary [Dinah Drake Lance]; Batman [Bruce Wayne]; Green Lantern [Hal Jordan]; The Atom [Ray Palmer]; Wonder Woman [Diana Prince]; Hawkgirl [Shayera Hol]; Red Tornado [John Smith]]; Justice Society of America [Wonder Woman; The Huntress [Helena Wayne]; Dr. Mid-Nite [Charles McNider]; Star-Spangled Kid [Sylvester Pemberton]; Superman [Kal-L]; The Flash [Jay Garrick]; Dr. Fate [Kent Nelson]; Green Lantern [Alan Scott]]; Black Pirate; Enemy Ace; Jonah Hex; Miss Liberty; Viking Prince; The Lord of Time; Traya', N'Joe Orlando', N'44', N'October 1978', N'', N'0709893055510', N'The annual Justice League of America/Justice Society of America get-together is interrupted by the arrival of five heroes from the past sent by the Lord of Time. The eight JLAers and JSAers who remain standing after the first attack head out to confront the time-tossed heroes, but find themselves overpowered. Meanwhile, in the far future, the Lord of Time roots for the super-heroes to regroup and defeat his agents.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2383, N'Justice League of America', N'', N'Justice League of America [Superman (Earth-1); Batman (Earth-1); Wonder Woman (Earth-1); Green Lantern [Hal Jordan]; Zatanna; Red Tornado [John Smith]; Hawkman [Katar Hol] (cameo); The Flash [Barry Allen] (cameo); Black Canary [Dinah Laurel Lance] (cameo); Green Arrow (Earth-1) (cameo); Elongated Man (cameo)]; Secret Society of Super-Villains [The Wizard; Blockbuster [Mark Desmond]; Floronic Man [Jason Woodrue]; Reverse-Flash [aka Professor Zoom, Eobard Thawne]; Star Sapphire [Camille]] (villains); Hi-Jack of the Royal Flush Gang (cameo)', N'Joe Orlando', N'36', N'June 1979', N'', N'0709893055506', N'The Justice League get to use the villains'' bodies and the Secret Society proceeds with their own plans as the JLA members try to get their bodies back and find the SSOSV.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2384, N'Justice League of America', N'', N'Justice League of America [Superman (Earth-1); Batman (Earth-1); Wonder Woman (Earth-1); Green Lantern [Hal Jordan]; Zatanna; Green Arrow (Earth-1); Hawkman [Katar Hol]; Black Canary [Dinah Laurel Lance]; Flash [Barry Allen]; Elongated Man [Ralph Dibny]; Red Tornado [John Smith]]; Secret Society of Super-Villains [Blockbuster [Mark Desmond]; Reverse-Flash [aka Professor Zoom, Eobard Thawne]; Floronic Man [Jason Woodrue]; The Wizard; Star Sapphire [Camille]]; Mr. Sloane (cameo); Ultraa (cameo)', N'Joe Orlando', N'36', N'July 1979', N'', N'0709893055507', N'The Justice League finally catches up with the body swapping Secret Society of Super-Villains.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2385, N'Justice League of America', N'', N'Justice League of America [The Flash [Barry Allen]; Hawkman [Katar Hol]; Red Tornado [John Smith]; Batman [Bruce Wayne]; Superman [Kal-El]; Zatanna; Green Lantern [Hal Jordan]]; Justice Society of America [Hawkman [Carter Hall]; Dr. Fate [Kent Nelson]; The Huntress [Helena Wayne]; Flash [Jay Garrick]; Green Lantern [Alan Scott]; Power Girl; Mr. Terrific [Terry Sloane]]; The Spirit King', N'Joe Orlando', N'36', N'October 1979', N'', N'0709893055510', N'At the annual Justice League of America-Justice Society of America get-together, Mr. Terrific claims that he''s come out of retirement to track his old enemy the Spirit King, but he won''t say any more until the right moment. Baffled by Mr. Terrific''s odd behavior, the heroes try to proceed with the event, but it isn''t long before there is a hole blown in the side of the side of the satellite and Mr. Terrific''s body is found floating in the vacuum of space.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2386, N'Justice League of America', N'', N'Justice League of America [The Flash [Barry Allen]; Hawkman [Katar Hol]; Red Tornado [John Smith]; Batman [Bruce Wayne]; Superman [Kal-El]; Zatanna; Green Lantern [Hal Jordan]]; Justice Society of America [Hawkman [Carter Hall]; Dr. Fate [Kent Nelson]; The Huntress [Helena Wayne]; Flash [Jay Garrick]; Green Lantern [Alan Scott]; Power Girl; Mr. Terrific [Terry Sloane] (deceased)]; The Spirit King', N'Joe Orlando', N'36', N'November 1979', N'', N'0709893055511', N'The two teams race to uncover Mr. Terrific''s murderer, the Spirit King, before another hero falls. Both Zatanna and Huntress are injured before the heroes determine that the villain, having a long-standing grudge against him as well, has possessed the JSA''s Flash. But before he can be apprehended, the Spirit King returns to Earth-2, leaving the JSA vowing to follow him and bring him to justice.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2387, N'Justice League of America', N'', N'Justice League of America [Superman (Earth-1); Batman (Earth-1); Flash [Barry Allen]; Green Lantern [Hal Jordan]; Wonder Woman (Earth-1); Hawkman [Katar Hol]; Hawkwoman [Shayera Hol]; Zatanna; Red Tornado [John Smith]]; Traya; Doctor Destiny (villain); Kathy Sutton', N'Joe Orlando', N'36', N'February 1980', N'', N'0709893055502', N'Red Tornado resigns from the League over the objections of his fellow heroes. He returns to New York and is  reunited with Traya, his former ward, and Kathy Sutton. Meanwhile, Doctor Destiny has escaped and is using his Materioptikon to force people to see monsters. Red Tornado fights the illusions and smashes the machine that''s causing them, but Destiny escapes before the rest of the Justice League arrives.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2388, N'Justice League of America', N'', N'Justice League of America [Superman (Earth-1); Green Lantern [Hal Jordan]; Wonder Woman (Earth-1); Hawkwoman [Shiera Hall]; Zatanna; Red Tornado]; Doctor Destiny (villain)', N'Joe Orlando', N'36', N'March 1980', N'', N'0709893055503', N'The Leaguers must split into three teams to track down Doctor Destiny''s three remaining Materoptikon machines before midnight or they will destroy the world.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2389, N'Justice League of America', N'', N'Justice League of America [Aquaman; Atom [Ray Palmer]; Batman (Earth-1); Black Canary; Green Arrow (Earth-1); Superman (Earth-1); Zatanna; Martian Manhunter [J''onn J''onzz] (cameo); Hawkman [Katar Hol] (statue); Hawkgirl [Shayera Hol] (statue); Red Tornado (statue); Green Lantern [Hal Jordan] (statue); Flash [Barry Allen] (statue); Wonder Woman (Earth-1) (statue); Elongated Man (statue)]; Commissioner James Gordon; Despero (villain) (cameo)', N'Joe Orlando', N'36', N'April 1980', N'', N'0709893055504', N'Separately, Aquaman is attacked by criminal dolphin fishers, Atom by kidnappers, Green Arrow and Black Canary by Nazis, and Superman and Batman by hijackers. Zatanna figures out that the attacks are all connected, as part of a strange game of chess. Elsewhere, J''onn J''onzz and Despero face each other over a chessboard.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2390, N'Justice League of America', N'', N'Justice League of America [Superman (Earth-1); Batman (Earth-1); Green Arrow (Earth-1); Black Canary [Dinah Laurel Lance]; Zatanna; Red Tornado [John Smith]; Firestorm [Ronnie Raymond; Martin Stein] (joins); Hawkman [Katar Hol] (cameo); Hawkgirl [Shayera Hol] (cameo); Aquaman (cameo); Wonder Woman (Earth-1, cameo); Elongated Man (cameo); Flash [Barry Allen] (cameo); Green Lantern [Hal Jordan] (cameo); Green Arrow (Earth-1, cameo); Martian Manhunter [J''onn J''onzz] (cameo flashback)]; The Satin Satan [Sabrina Sultress] (villain); Doreen Day; Cliff Carmichael; Cal Remington; Rosco Remington (cameo); Black Lightning [Jefferson Pierce] (cameo flashback)', N'Joe Orlando', N'36', N'June 1980', N'', N'0709893055506', N'With Superman''s sponsorship, Firestorm is voted into the League. Later, Ronnie hears from his friends that Cal''s brother vanished from a discotheque under mysterious circumstances. He investigates as Firestorm and is captured by the Satin Satan.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2391, N'Justice League of America', N'', N'Justice League of America [Green Arrow (Earth-1, quits); Black Canary [Dinah Laurel Lance]; Elongated Man [Ralph Dibny]; Hawkgirl [Shayera Hol]; Aquaman [Arthur Curry]; Flash [Barry Allen]; Superman (Earth-1); Atom [Ray Palmer]; Wonder Woman (Earth-1) (flashback)]; Star-Tsar [Richard Rigel] (villain); Snapper Carr; Donna Winter', N'Joe Orlando', N'36', N'August 1980', N'', N'0709893055508', N'Green Arrow and Black Canary are attacked by the Star-Tsar, and Dinah is seriously injured. Snapper Carr tells the Justice League that his Star-Tsar costume was stolen and that identity has now been assumed by astronomer Richard Rigel. After Rigel is captured by the League, Green Arrow resigns from the team.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2392, N'Justice League of America', N'', N'Justice League of America [Atom [Ray Palmer]; Batman (Earth-1); Black Canary; Green Arrow (Earth-1); Hawkman [Katar Hol]; Red Tornado]; The Siren Sisterhood [Mother Moon; Sister Light; Twigg]; Brother Mule; Kass''andre (villain); Krystal Kaa [Wanderer]; Goltha (villain)', N'Len Wein', N'36', N'June 1983', N'', N'', N'The Atom, now a mindless giant; drives Hawkman and Red Tornado out of Goltha''s fortress city but they are rescued by the Siren Sisterhood. Krystal Kaa tells the League how her parents were murdered by Goltha, and that she needs to retrieve the Staff of Ikara, a talisman that will prove her to be the rightful heir to the throne. The Sisterhood leads Krystal and the Justice League through a secret tunnel back into the city, but they are confronted by Goltha''s daughter Kass''andre, who now holds the Staff of Ikara.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2393, N'Justice League of America', N'', N'Justice League of America [The Atom [Ray Palmer]; Batman; Hawkman [Katar Hol]; Snapper Carr; Superman; Wonder Woman]; Dr. Destiny (villain); The Joker (dream version, villain); Chac (dream version, villain)', N'Julius Schwartz', N'36', N'March 1965', N'', N'', N'Five JLAers see their own defeats in their dreams, but when the same scenarios occur to them while awake, they successfully overcome the challenges.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2394, N'Justice League of America', N'', N'Justice League of America [Atom [Ray Palmer]; Batman [Bruce Wayne]; Green Lantern [Hal Jordan]; Hawkman [Katar Hol; Carter Hall]; Superman [Clark Kent; Kal-El] (all Earth-1)]; Justice Society of America [Black Canary [Dinah Drake Lance]; Dr. Fate [Kent Nelson; Nabu]; Dr. Mid-Nite [Charles McNider]; Green Lantern [Alan Scott]; Starman [Ted Knight]; Superman [Clark Kent; Kal-L] (intro); Wonder Woman [Diana Prince Trevor]; Red Tornado [John Smith]; Larry Lance (all Earth-2); Council of Living Stars (cameo); Aquarius (villain)', N'Julius Schwartz', N'36', N'August 1969', N'', N'', N'Starman relays his story to the JSA of a new menace, Aquarius, a stellar being who had been disincorporated after his evil acts, and sensing Starman''s Cosmic Rod''s energies, steals the rod from him to reincorporate himself. After nearly being defeated by the JSA, Aquarius uses the rod to blink Earth-2 out of existence, but not before several members protect themselves in an energy bubble using Fate''s magicks and GL''s ring''s mystic energies. Red Tornado, meanwhile, has been sent to Earth-1 to the JLA to get help battling Aquarius. To be continued...', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2395, N'Justice League of America', N'', N'Justice League of America [JLA; Atom [Ray Palmer]; Batman [Bruce Wayne]; Black Canary [Dinah Drake]; Green Arrow [Oliver Queen]; Green Lantern [Hal Jordan]; Hawkman [Katar Hol; Carter Hall]; Superman [Clark Kent; Kal-El]]; The Vigilante [Greg Sanders]; Jason Crass; Chokh (villain); The Doomsters (first appearance; villains)', N'Julius Schwartz', N'36', N'February 1970', N'', N'', N'The Vigilante comes out of retirement to enlist the JLA''s help to discover the secret behind a factory that produces nothing but air and water pollution. The League moves into their new satellite headquarters, which is located 22,300 miles above the United States.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2396, N'Justice League of America', N'', N'Justice League of America [JLA; Atom [Ray Palmer]; Batman [Bruce Wayne]; Black Canary [Dinah Drake]; Green Arrow [Oliver Queen]; Green Lantern [Hal Jordan]; Hawkman [Katar Hol; Carter Hall]; Superman [Clark Kent; Kal-El]]; The Vigilante [Greg Sanders]; Jason Crass; Chokh (villain); The Doomsters (villains)', N'Julius Schwartz', N'36', N'March 1970', N'', N'', N'The heroes of the League discover that the polluting factory is an alien plot to conquer the planet.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2397, N'Justice League of America', N'', N'Justice League of America [JLA; Atom [Ray Palmer]; Batman [Bruce Wayne]; Black Canary [Dinah Drake]; Flash [Barry Allen]; Green Arrow [Oliver Queen]; Green Lantern [Hal Jordan]; Hawkman [Katar Hol; Carter Hall]; Superman [Clark Kent; Kal-El]]; Hawkgirl [Shayera Thal; Shiera Hall]; Jean Loring; Norch Lor (villain); Guardians of the Universe; Green Lantern [Tomar-Re]; Iris Allen (cameo)', N'Julius Schwartz', N'36', N'May 1970', N'', N'', N'Thanagarian criminal Norch Lor comes to Earth after defeating Hawkman and Hawkgirl, using the forbidden Ghenna Box to steal the consciousness of several Earth people and even some members of the League.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2398, N'Justice League of America', N'', N'Justice Leagie of America [Black Canary; Superman; Batman; The Flash [Barry Allen]; The Atom [Ray Palmer]; Hawkman [Katar Hol]; Green Lantern [Hal Jordan] (cameo); Green Arrow (cameo)]; Jest-Master (villain); Jean Loring; Ra Laf; Norch Lor; Guardians of the Universe (cameo)', N'Julius Schwartz', N'36', N'June 1970', N'', N'', N'Hawkman and Atom travel to Thanagar, hoping to find a cure for the insane Jean Loring.  They arrive as the planet is attacked by the Jest-Master, who drives people insane.  This inadvertently cures Jean, who then helps the League against their enemy.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2399, N'Justice League of America', N'', N'Justice League of America [Green Lantern [Hal Jordan]; Superman [Clark Kent; Kal-El]; Batman [Bruce Wayne]; Hawkman [Katar Hol]; The Flash [Barry Allen]; The Atom [Ray Palmer]; Green Arrow [Oliver Queen]; Black Canary [Dinah Lance]; Wonder Woman [Diana Prince] (flashback)]; Crime Syndicate [Owlman (flashback); Superwoman (flashback)]; Justice Society of America [Hourman [Rex Tyler]; Green Lantern [Alan Scott]; Starman [Ted Knight]; Dr. Fate [Kent Nelson; Nabu]; Wonder Woman [Diana Prince Trevor] (cameo); The Atom [Al Pratt] (cameo); Robin [Dick Grayson] (cameo); Sandman [Wesley Dodds] (cameo); Dr. Mid-Nite [Charles McNider]; Hawkman [Carter Hall]; The Spectre [Jim Corrigan]; The Flash [Jay Garrick]; Wildcat [Ted Grant]; Superman [Clark Kent; Kal-L]; Batman [Bruce Wayne] (cameo); Johnny Thunder (cameo); Mr. Terrific (cameo); Red Tornado [John Smith]]; Jimmy Olsen (cameo); Creator2 (villain)', N'Julius Schwartz', N'36', N'August 1970', N'', N'', N'A new menace threatens both Earths 1 and 2 as Red Tornado is captured by a foe known only as Creator2. Using his unique vibrational energies, Creator2 targets both the JSA and JLA separately, as each world''s doppelganger falls prey to Creator2''s traps. To be continued...', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2400, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Batman; Black Canary [Dinah Drake Lance]; Green Arrow [Oliver Queen]; The Atom [Ray Palmer]; Green Lantern [Hal Jordan]; Hawkman [Katar Hol]] (of Earth-1); Justice Society of America [Green Lantern [Alan Scott]; Superman [Clark Kent; Kal-L]; Hawkman [Carter Hall]; Dr. Mid-Nite; The Flash [Jay Garrick]; Johnny Thunder; Thunderbolt; Starman [Ted Knight]; Hourman [Rex Tyler]; Dr. Fate [Kent Nelson; Nabu]; Wonder Woman [Diana Prince Trevor]; The Spectre [Jim Corrigan]; Red Tornado [John Smith]] (of Earth-2); Creator2 (villain)', N'Julius Schwartz', N'36', N'September 1970', N'', N'', N'As the non-fallen members of both teams regroup to fight Creator2, the JSA decides to use magic against Creator2''s science in order to defeat him, which works, as Red Tornado also breaks free from Creator2''s hold on him to turn the tables and save the day. The then-fallen Earth 1 and 2 doppelganger heroes recover afterwards.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2401, N'Justice League of America', N'', N'Superman; Batman; Black Canary; The Atom [Ray Palmer]; The Flash [Barry Allen]; Hawkman [Katar Hol]; Professor Lars Hansun; Dr. Viktor Willard (villain); Nether Man (villain); Phyllis Temple; Iris Allen (cameo)', N'Julius Schwartz', N'36', N'November 1970', N'', N'', N'Dr. Viktor Willard''s Pax Serum is supposed to turn everyone on Earth peaceful, but only Willard knows that one hundred hours later they will all turn to savagery and destroy each other, leaving him and his wife as the only survivors to start civilization again.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2402, N'Justice League of America', N'', N'The Flash [Barry Allen]; Batman; Superman; Wonder Woman; Martian Manhunter; Aquaman; Green Lantern [Hal Jordan]; Green Arrow [Oliver Queen]; Snapper Carr; Felix Faust (villain); Demons Three [Abnegazar (villain); Rath (villain); Ghast (villain)]; the Lord of Time (villain); the Timeless Ones (flashback)', N'E. Nelson Bridwell', N'68', N'November-December 1970', N'', N'', N'Felix Faust learns that the Demons Three -- Abnegazar, Rath, and Ghast -- can only be freed from a spell involving the Red Jar of Calythos, the Green Bell of Uthool, and the Silver Wheel of Nyorlath, which were hidden long ago by their jailers, the Timeless Ones. Meanwhile, the Lord of Time unleashes warriors of the past and future on the present to steal for him. As the League tries to stop him, they disappear one by one and find themselves under the control of Felix Faust, who orders them to retrieve the items needed to free the Demons Three.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2403, N'Justice League of America', N'', N'Justice League of America [Superman; Green Lantern [Hal Jordan]; Green Arrow; The Flash [Barry Allen]; Batman; Hawkman [Katar Hol]; The Atom [Ray Palmer]; Aquaman]; Mera (cameo); Aqualad (cameo); The Zapper [Theo Zappa] (villain); Pajna Darr', N'Julius Schwartz', N'36', N'December 1970', N'', N'', N'Industrialist Theo Zappa devises a way to steal all the plankton on Earth in order to sell it to the planet Kalyarna, which has destroyed its own ecosystem with pollution. Aquaman alerts the League to the situation and the heroes split up into teams to stop Zappa from dooming Earth to starvation.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2404, N'Justice League of America', N'', N'Justice League of America [Hawkman [Katar Hol]; Batman; Superman; The Flash [Barry Allen]; The Atom [Ray Palmer]; Green Lantern [Hal Jordan]]; Heroes of Angor [Jack B. Quick; Silver Sorceress; Blue Jay; Wandjina]; Zatanna (guest star); Glugg (villain); The Master-Computer (villain)', N'Julius Schwartz', N'36', N'February 1971', N'', N'', N'The League tracks the origin of a robot which took mental control over Batman and Hawkman and they end up in a mistaken battle on a faraway planet with another group of heroes.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2405, N'Justice League of America', N'', N'Justice League of America [Green Lantern [Hal Jordan]; Hawkman [Katar Hol]; Aquaman [Arthur Curry]; Superman [Clark Kent]; Batman [Bruce Wayne]; Black Canary [Dinah Laurel Lance]; The Atom [Ray Palmer]; Green Arrow]; Hawkgirl [Shiera Hall; Shayera Hol]; Mera; Omar Ibn Ali; Lok Lu; Pete Conk; People of Mu (villains)', N'Julius Schwartz', N'36', N'March 1971', N'', N'', N'Thousands of years ago, the survivors of the sunken continent of Mu left Earth in a giant spaceship.  Now they are returning and have decided to wipe out the current population of the planet.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2406, N'Justice League of America', N'', N'Justice League of America [Superman; Hawkman [Katar Hol]; Aquaman; Batman; The Atom [Ray Palmer]; The Flash [Barry Allen]; Green Lantern [Hal Jordan]]; Mike Friedrich (cameo); Harlequin Ellis; a cyclops; a minotaur', N'Julius Schwartz', N'36', N'May 1971', N'', N'', N'Writer Harlequin Ellis is infatuated with Black Canary, and has inexplicable dreams in which he becomes Superman and Batman and causes Green Arrow and other Leaguers to be injured or killed.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2407, N'Justice League of America', N'', N'Justice League of America [Superman [also appears as Clark Kent]; Batman; Green Lantern [Hal Jordan]; Hawkman [Katar Hol]; The Atom [Ray Palmer]; Aquaman; The Flash [Barry Allen] (cameo)]; Mera; Shally (dies); the Pale People; Nebeur Odagled', N'Julius Schwartz', N'36', N'June 1971', N'', N'', N'The United States dumps a shipment of poison gas into the ocean, which destroys the Proof-Rock of the Pale People and leads them to attack Atlantis.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2408, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Hawkman [Katar Hol]; Green Lantern [Hal Jordan]; The Atom [Ray Palmer]; The Flash [Barry Allen]; Batman [Bruce Wayne]; Black Canary [Dinah Lance]; Green Arrow [Oliver Queen]] (of Earth-1); Justice Society of America [Superman [Clark Kent; Kal-L]; The Flash [Jay Garrick]; Hawkman [Carter Hall]; The Atom [Al Pratt]; Robin [Dick Grayson]; Green Lantern [Alan Scott]] (of Earth-2); Robin [Dick Grayson] (of Earth-1) (guest star); S-Kyr; A-Rym; Teppy; E-Hes; Solomon Grundy [Cyrus Gold] (villain)', N'Julius Schwartz', N'52', N'August 1971', N'', N'', N'Three aliens are out on a joyride alongside their symbiotic pets when they lose control of their ship, resulting in the alien A-Rym being separated from his pet as they travel through the dimensional barrier separating Earths 1 and 2. As the JSA face off against A-Rym, he defeats them, as a result of the adult Robin charging in headstrong. Meanwhile, on Earth-1, as Batman leaves the JLA for another case he''s working on, young college-aged Robin faces off alongside the team vs. Teppy, A-Rym''s symbiotic pet, with the same actions and consequences. To be continued...', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2409, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent; Kal-El]; Green Lantern [Hal Jordan]; Hawkman [Katar Hol]; The Atom [Ray Palmer]; The Flash [Barry Allen]; Black Canary (cameo); Batman (cameo); Green Arrow (cameo); Aquaman (cameo)]; Justice Society of America [Superman [Clark Kent; Kal-L]; Green Lantern [Alan Scott]; Hawkman [Carter Hall]; The Atom [Al Pratt]; The Flash [Jay Garrick]; Robin [Dick Grayson]]; Robin (guest star); Solomon Grundy [Cyrus Gold] (villain); A-Rym; Teppy; S-Kyr; E-Nes; Iris Allen (cameo)', N'Julius Schwartz', N'52', N'September 1971', N'', N'', N'As A-Rym encounters Solomon Grundy on Earth-2, the JLA meets up with the JSA to try and stop A-Rym''s rampage. After, the Earth-2 Hawkman sends both Robins away to the Batcave. While there, the Earth-2 Robin gives his Earth-1 doppelganger a new costume to replace his damaged one. Meanwhile the teams realize that if A-Rym and Teppy aren''t reunited soon, they''ll die, and both teams scramble to reunite them. After A-Rym and Teppy return home, the teams part, with Batman waiting for the League to aid him on his case and the Earth-1 Robin wondering if he should continue to wear his new costume.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2410, N'Justice League of America', N'', N'Justice League of America [Green Lantern [Hal Jordan]; Martian Manhunter; Wonder Woman; Green Arrow [Oliver Queen]; The Flash [Barry Allen]; Batman; Superman; Aquaman; Snapper Carr (cameo)]; Speedy [Roy Harper] (cameo); Thomas Kalmaku [Pieface] (cameo); Jimmy Olsen (cameo); Sforll; Zed Brann (villain, flashback); Ragnar (flashback)', N'E. Nelson Bridwell', N'68', N'October-November 1971', N'', N'', N'Sforll transports the Justice League to his lair to enlist them in preventing Zed Brann from stealing the life force ("vita-matter") of our universe for his own. They must fight robot duplicates of themselves to save the universe.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2411, N'Justice League of America', N'', N'Justice League of America [Green Arrow [Oliver Queen]; Aquaman; Batman [Bruce Wayne]; Green Lantern [Hal Jordan] (photo); The Flash [Barry Allen] (cameo); Hawkman [Katar Hol] (cameo); Black Canary [Dinah Laurel Lance] (cameo); Superman [Clark Kent]; The Atom [Ray Palmer]]; Deadman [Boston Brand] (guest star); Sensei (villain); League of Assassins (villains); Merlyn [Arthur King] (intro, villain); Morgan Edge (cameo); Inter-Gang (cameo)', N'Julius Schwartz', N'52', N'November 1971', N'', N'', N'The Sensei, head of the League of Assassins, sends the archer Merlyn to kill Batman.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2412, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent]; Batman [Bruce Wayne]; Aquaman (cameo); Black Canary [Dinah Laurel Lance]; Green Lantern [Hal Jordan] (cameo); Hawkman [Katar Hol] (cameo); The Flash [Barry Allen] (cameo); The Atom [Ray Palmer]; Green Arrow [Oliver Queen]]; Johnny Dune (villain)', N'Julius Schwartz', N'52', N'December 1971', N'', N'', N'Returning war veteran Johnny Dune discovers that he has the mutant power to control people with his voice.  Though he tries to use this ability for his own advancement, a confrontation with the Justice League leads him to make a grave sacrifice.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2413, N'Justice League of America', N'', N'Justice League of America [Superman; The Flash [Barry Allen]; Hawkman [Katar Hol]; Green Lantern [Hal Jordan]]; Starbreaker (villain); Adam Strange (cameo); Alanna (cameo)', N'Julius Schwartz', N'52', N'February 1972', N'', N'', N'Superman joins his comrades on the planet Rann, where they were transported by a stray Zeta-Beam.  There they encounter indestructible metal insects sent by Starbreaker, a new menace who can absorb the power of suns.', N'', N'DC', N'1449')
