INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2415, N'Justice League of America', N'', N'Justice League of America [Superman [Clark Kent]; The Flash [Barry Allen]; Green Lantern [Hal Jordan]; Aquaman; The Atom [Ray Palmer]; Black Canary [Dinah Laurel Lance]; Green Arrow [Oliver Queen]; Hawkman [Katar Hol]; Batman [Bruce Wayne]]; Sargon the Sorcerer (guest star); Starbreaker (villain); Hawkwoman [Shayera Hol] (as Hawkgirl, cameo); Supergirl [Kara Zor-El] (cameo)', N'Julius Schwartz', N'52', N'May 1972', N'', N'', N'Sargon the Sorcerer offers the League his help in defeating Starbreaker, and sends the team on missions to obtain the magical artifacts they will need.  The reunited League with the help of Sargon, Supergirl, amd Hawkgirl capture Starbreaker and send him to be imprisoned by the Guardians of the Universe.', N'', N'DC', N'1449')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2416, N'Justice League of America', N'', N'Justice League of America [Superman; Black Canary; The Flash [Barry Allen]; The Atom [Ray Palmer]; Hawkman [Katar Hol]; Green Lantern [Hal Jordan]; Batman; Aquaman]; Bur Sed (villain); Ker Sed (villain); Lois Lane (cameo)', N'Julius Schwartz', N'52', N'June 1972', N'', N'', N'A father-son team of aliens have come to Earth to sow seeds that will flower into giant plants intended to eliminate pollution.  However, the plants pose different dangers to human life and the League must stop the misguided aliens.', N'', N'DC', N'1449')
