INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2418, N'Marvel''s Greatest Creators: Iron Fist - Colleen Wing', N'', N'Iron Fist [Danny Rand]; Colleen Wing (introduction); Professor Lee Wing [also as the Ninja]; Ward Meachum (introduction, villain); Death-Cult of Kara-Kai (introduction, villains); Joy Meachum; Yu-Ti (flashback); Lei Kung, the Thunderer (cameo flashback); Da Tempa (dies in flashback)', N'Roy Thomas', N'32', N'July 2019', N'', N'75960609299400111', N'Iron Fist meets Colleen Wing and her father who knows all about Danny''s stay in K''un-Lun. Iron Fist tries to make amends with Joy Meachum but she leads him into a trap where he has to face members of the Kara-Kai death cult. The mysterious ninja who actually killed Meachum shows up to give Danny a hand.', N'', N'Marvel', N'145028')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2419, N'Zatanna', N'', N'Zatanna; Sindella; John Constantine (all in a dream sequence); Joshua, Marsha‘s son; Ron, Zatanna’s current beau; Marsha, a friend of Zatanna‘s; Apollonius; Tannarak', N'Paul Kupperberg', N'36', N'July 1993', N'', N'', N'Disturbing dreams bother Zatanna as she goes through a typical day in her life: friends, romance, and work. Seeking information about the dreams from Tannarak, she finds no answers as the veil between the real world and the supernatural planes begin to lift.', N'', N'DC', N'14525')
