INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (2426, N'Daredevil', N'The Murdock Papers', N'Daredevil [Matt Murdock]; Elektra (imaginary); Black Widow [Natasha Romanova] (imaginary); Foggy Nelson; Reed Richards [Mr. Fantastic]; Milla Donovan (imaginary); Director Hill; Kingpin; The Owl; Hammerhead; Bullseye (imaginary); Luke Cage [Power Man]; Jesica Jones; Steve Rogers [Captain America]; Peter Parker [Spider-Man]; Brian Michael Bendis; Alex Maleev', N'Axel Alonso', N'156', N'[April] 2006', N'9780785118107', N'978078511810751499', N'In court facing federal charges, Matt imagines what it would be like to bust out of the courtroom and try to find a new life in a far away place with the woman he loves. Doing the noble thing, however, he remains to face the music only to be consigned to Ryker''s Island awaiting trial. Kingpin is double crossed by the feds and also sent to Rykers. Daredevil is in jail. Kingpin is in jail. Lots of other baddies are in jail. Together.', N'', N'Marvel', N'14558')
