INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3234, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Doctor Octopus [Otto Octavius] (Introduction; Origin); Human Torch [Johnny Storm]; Charlie (Burglar); J Jonah Jameson; Aunt May Parker; Flash Thompson; Liz Allen', N'Stan Lee', N'36', N'July 1963', N'', N'', N'When an experiment goes wrong, Otto Octavius gains control of his robotic arms and decides to take over an atomic facility, but Spider-Man stops him.', N'', N'Marvel', N'1570')
