INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3273, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Cardiac (introduction); Mary Jane Parker; VILLAINS: Rhino; Justin Hammer; Cletus Kasady (introduction, cameo, becomes Carnage in ASM #361); Eddie Brock [Venom]', N'Dan Cuddy', N'36', N'February 1991', N'', N'', N'Spidey investigates a stash of chemicals used to process cocaine and runs up against Cardiac (who claims he''s a good guy) and Rhino (who claims that he isn''t). The owner of the chemicals, Justin Hammer, decides the best way to beat Spidey is to hire him.', N'', N'Marvel', N'1570')
