INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3281, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Mary Jane Parker; Aunt May Parker; Nova [Richard Rider]; Tri-Sentinel (villain); Carlton Drake (villain); Life Foundation (villain)', N'Eric Fein', N'40', N'September 1991', N'', N'', N'Spidey and Nova witness the robbery of a container of rare Antarctic vibranium. They trace the thieves back to a complex run by the Life Foundation and discover that the vibranium is being used as a fail safe for the Tri-Sentinel which the Foundation plans on using for their own protection. Despite Spidey''s protests, the Tri-Sentinel is activated and immediately reverts to its original programming, the destruction of nuclear power plants. With the deadly robot on a rampage, Spidey and Nova have a fight on their hands.', N'', N'Marvel', N'1570')
