INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3300, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; J. Jonah Jameson; Ned Leeds; Harry Osborn; Norman Osborn; Flash Thompson; Gwen Stacy; Aunt May Parker; Joe Smith (villain, introduction); Mary Jane Watson', N'Stan Lee', N'36', N'July 1966', N'', N'', N'Average Joe, Joe Smith, fails at being a fighter and tries his hand at acting. Dressed up as a robotic monster, he is zapped with electricity and chemicals which causes him to go on a violent spree against those who doubted his abilities. Meanwhile, Norman Osborn puts a price on Spidey''s head and our hero is plagued with goons trying to bump him off. Pete narrowly misses meeting Mary Jane.', N'', N'Marvel', N'1570')
