INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3341, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Ka-Zar [Kevin Plunder]; Zabu; Aunt May Parker; Anna Watson; Captain George Stacy; John Jameson; J. Jonah Jameson; Harry Osborn', N'Stan Lee', N'36', N'February 1968', N'', N'', N'Spider-Man is still dealing with the loss of his memory; meanwhile, J. Jonah Jameson enlists the aid of Ka-Zar to capture Spider-Man.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3342, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Lizard [Curt Connors] (introduction, origin); J. Jonah Jameson; Flash Thompson; Aunt May Parker; Betty Brant; Liz Allen; Martha Connors; Billy Connors; Vulture [Adrian Toomes] (cameo)', N'Stan Lee', N'36', N'November 1963', N'', N'', N'The Bugle challenges Spider-Man to put an end to the Lizard''s rampage.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3343, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Mary Jane Watson; Gwen Stacy; Harry Osborn; Captain George Stacy; J. Jonah Jameson; Norman Osborn; Montgomery G. Bliss; Wilberforce; Medusa', N'Stan Lee', N'36', N'July 1968', N'', N'', N'Medusa travels to New York City to gauge the reaction of the common person to the presence of an Inhuman.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3344, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Kingpin; Robbie Robertson; Randy Robertson; J. Jonah Jameson; Gwen Stacy; Mr. Stacy (Gwen''s father)', N'Stan Lee', N'36', N'February 1969', N'', N'', N'Spidey goes heads-up with the strength of the Kingpin, who is knocked out when his own cane-gun backfires. The police arrest the Kingpin for theft of the priceless tablet, but he claims that his ally Spider-Man has spirited it away. Meanwhile, Spidey manages to open the vault and retrieve the tablet, but when he tries to return it the police open fire on him.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3345, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Vulture [Adrian Toomes]; Flash Thompson; Aunt May Parker; Betty Brant; J. Jonah Jameson; Liz Allen', N'Stan Lee', N'36', N'November [December] 1963', N'', N'', N'Adrian Toomes breaks out of prison and goes on a crime spree, and ends up battling Spider-Man in the skies above New York.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3346, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Kingpin; J. Jonah Jameson; Gwen Stacy; Captain Stacy; Robbie Robertson; Randy Robertson; Josh; Dean Corliss; Ned Leeds', N'Stan Lee', N'36', N'March 1969', N'', N'', N'Still on the lam, Spidey hides the tablet in Peter''s closet. The Kingpin breaks jail vowing to get Spider-Man and the tablet. Spidey obliges him and the two battle. In the middle of the battle, Jonah driven by Ned Leeds appears and calls for the police. A mysterious car with a woman driver appears and the Kingpin gets away, unsatisfied. Faced with yet another rant from Jameson, Spider-Man turns and starts to rant right back causing Jonah to have an apparently fatal heart attack. Is Spider-Man a murderer!?', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3347, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Harry Osborn; J. Jonah Jameson; John Jameson; Ned Leeds; Edwin Jarvis; Robbie Robertson; Martha Robinson; Randy Robertson; Betty Brant; Captain Stacy; Quicksilver; Scarlet Witch (cameo flashback); Toad (cameo flashback); Kingpin (cameo flashback)', N'Stan Lee', N'36', N'April 1969', N'', N'', N'With Jonah down for the count, Robbie publishes Peter''s pictures in the Bugle showing Kingpin, and not Spider-Man, to be the real crook in the case of the golden tablet. Trying to redeem himself after his recent career with Magneto, Quicksilver decides to bring in the menace known as Spider-Man but discovers he isn''t the villain the Bugle has painted him to be.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3348, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Lizard [Dr. Connors]; Martha Connors; Billy Connors; Harry Osborn; Gwen Stacy; Maggia (villain); Silvermane (villain); Caeser Cicero (villain); Man-Mountain Marko (villain)', N'Stan Lee', N'36', N'July 1969', N'', N'', N'Silvermane believes that the petrified tablet contains the secret of the Fountain of Youth. He forces Dr. Connor to interpret the tablet and make the serum described therein. Silvermane drinks the untested serum - and it works!', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3349, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Lizard [Dr. Connors]; Man-Mountain Marko; Silvermane (death); Caeser Cicero; Martha Connors; Billy Connors', N'Stan Lee', N'36', N'August 1969', N'', N'', N'Spidey, aware that Dr. Connors and his family are in danger from the Maggia, searches for them. The once-elderly Silvermane tries to convince Marko and Cicero that he is in fact a younger version of himself while Dr. Connors escapes and Spider-Man arrives. In the ensuing free-for-all Connors transforms into the Lizard ... and Silvermane just keeps getting younger.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3350, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Lizard [Curt Connors] (villain); Billy Connors; Martha Connors; Aunt May Parker; Harry Osborn; Gwen Stacy; Robbie Robertson; Captain George Stacy; Human Torch [Johnny Storm]', N'Stan Lee', N'36', N'September 1969', N'', N'', N'The Lizard is at large in New York and Captain Stacy wants to talk to Peter about Spider-Man''s secret identity.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3351, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Lizard [Doctor Curt Connors] (villain); Human Torch [Johnny Storm]; Billy Connors; Martha Connors', N'Stan Lee', N'36', N'October 1969', N'', N'', N'The Human Torch complicates Spider-Man''s plan to catch the Lizard without hurting him.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3352, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Gwen Stacy; Flash Thompson; J. Jonah Jameson; Robbie Robertson; Prowler [Hobie Brown] (introduction, origin)', N'Stan Lee', N'36', N'November 1969', N'', N'', N'Peter tries to see Gwen only to find that she is (apparently) on a date with Flash. Hobie approaches his boss with ideas for new safety devices for his job as window-washer, only to be rebuffed. He implements them in private, adds a cloak and a mask, and thus creates The Prowler. Hobie plans to rob a newspaper as the Prowler then return the money as Hobie, making himself a hero. Naturally Peter (as Peter) discovers the Prowler in the act and J. Jonah discovers Peter in front of an empty safe.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3353, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; The Prowler [Hobie Brown]; J. Jonah Jameson; Robbie Robertson; Gwen Stacy; Mindy (Hobie''s Girlfriend, reference only)', N'Stan Lee', N'36', N'December 1969', N'', N'', N'Spidey and the Prowler fight. The Prowler gets away using a gas bomb, then plans another robbery to make himself (Hobie) a hero when the loot is recovered. Peter anticipates the move and gets there during the robbery. This time the Prowler''s gas has no effect and Peter unmasks him to find a well-intentioned kid like himself. Spidey lets him go.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3354, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Living Brain; Flash Thompson; Liz Allen; Mr. Petty; Mr. Warren; J. Jonah Jameson (cameo); Aunt May Parker (cameo); Vulture [Adrian Toomes] (cameo)', N'Stan Lee', N'36', N'January 1964', N'', N'', N'When two technicians try to steal a computer that is on display at Peter''s school, they accidentally turn it into "The Living Brain" that goes wild and must be stopped by Spider-Man.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3355, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Harry Osborne; Flash Thompson; Gwen Stacy; Capt. Stacy; J. Jonah Jameson; Robbie Robertson; the Chameleon', N'Stan Lee', N'36', N'January 1970', N'', N'', N'Harry brings Flash to Peter''s apartment and forces them to work out the confusion about Gwen. Peter and Gwen reunite and attend a gala where paintings are stolen under the nose of Capt. Stacy - it is the work of the Chameleon. Spidey sets a trap for the Chameleon but fails to unmask him until he sees ... Peter Parker!', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3356, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; J. Jonah Jameson; Aunt May Parker; The Kangaroo (introduction, origin)', N'Stan Lee', N'36', N'February 1970', N'', N'', N'Aunt May decides Peter is sick and puts him to bed. The Kangaroo escapes Feds who seek to deport him, then steals what he thinks is money but is actually a vial of deadly bacteria. Peter sneaks out to catch the Kangaroo and get the vial back. He succeeds, but Aunt May collapses when she finds the web dummy Peter left in his bed. Now Peter feels more guilt than ever.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3357, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Mary Jane Watson; Mrs. Watson (MJ''s aunt Anna); J. Jonah Jameson; Electro [Max Dillon] (origin recap); Captain Stacy; Flash Thompson; Harry Osborne; Robbie Robertson; Marvin (talk show host)', N'Stan Lee', N'36', N'March 1970', N'', N'', N'Peter decides that it would be good for his public image to go on a TV talk show. Unfortunately he chooses a network where Max Dillon [Electro] works. In collusion with Jameson, Electro attacks Spider-Man live on stage. Spidey defeats him, but it is a Pyrrhic victory - Peter is as beat up as Max.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3358, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Schemer; Kingpin; Vanessa Fisk; Richard Fisk (photo); J. Jonah Jameson; Robbie Robertson; Betty Brant; Doctor Bromwell; Gwen Stacy; Flash Thompson; Harry Osborn; Captain Stacy', N'Stan Lee', N'36', N'April 1970', N'', N'', N'The Schemer tries to become the new King of Crime in New York, and starts a war with the Kingpin. Flash Thompson goes to war in Vietnam and Gwen Stacy is injured in a traffic accident.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3359, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Schemer; Gwen Stacy; Captain George Stacy; Kingpin; Vanessa Fisk', N'Stan Lee', N'36', N'May 1970', N'', N'', N'Peter could use the $5,000 reward, so Spidey sets out to capture The Schemer.  He eventually finds him in his fancy car but keeps falling off it.  Schemer arrives at the Kingpin''s mansion and confronts him; Vanessa intervenes.  Spider-Man has followed Schemer, breaks in, and has a brief tiff with Kingpin before both realize that Schemer - and Vanessa - have vanished.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3360, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; GUESTS: Vanessa Fisk; Captain Stacy; Gwen Stacy; VILLAINS: Kingpin; Schemer [Richard Fisk; identity revealed]', N'Stan Lee', N'36', N'June 1970', N'', N'', N'Peter pulls a double switch to keep Captain Stacy and Gwendy from identifying him as Spider-Man. Spidey tracks down the Schemer but they both fall into the Kingpin''s trap. The ensuing battle comes to an abrupt end when the Schemer is revealed to be Richard Fisk, the Kingpin''s long lost son.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3361, N'The Amazing Spider-Man', N'', N'Spider-Man [also appears as Peter Parker]; Black Widow [Natasha Romanova]; Gwen Stacy; Captain George Stacy; Harry Osborn; J. Jonah Jameson; Aunt May Parker; Anna May Watson; Hawkeye (cameo flashback); Iron Man [Tony Stark] (cameo flashback); Crimson Dynamo (cameo flashback); Red Guardian [Alexei Shostakov](cameo flashback); Nick Fury (cameo flashback)', N'Stan Lee', N'36', N'July 1970', N'', N'', N'Natasha Romanova returns to New York with a new costume and decides to test herself by fighting Spider-Man, who defeats her even though he is seriously ill.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3362, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Gwen Stacy; J. Jonah Jameson; John Jameson; Robbie Robertson; Betty Brant; Captain George Stacy; Doctor Octopus (villain)', N'Stan Lee', N'36', N'September 1970', N'', N'', N'From his prison cell, Doctor Octopus calls to his mechanical arms and even Spidey can''t stop the inevitable reunion. Doctor Octopus hijacks a plane and holds an Asian general hostage for ten million bucks but Spidey foils his plans.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3363, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Randy Robertson; J. Jonah Jameson; Robbie Robertson; Dr. Octopus [Otto Octavius] (villain)', N'Stan Lee', N'36', N'October 1970', N'', N'', N'Doc Ock versus Spider-Man.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3364, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Electro [Max Dillon] (villain, introduction, origin); Aunt May Parker; Betty Brant; J. Jonah Jameson; Flash Thompson; Liz Allen', N'Stan Lee', N'36', N'February 1964', N'', N'', N'Electro decides to break some other criminals out of jail so they can help him with his crime spree. Spider-Man must balance taking care of Aunt May, who is in the hospital, and capturing Electro.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3365, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Iceman [Bobby Drake]; Gwen Stacy; J. Jonah Jameson; Robbie Robertson; Sam Bullit (villain)', N'Stan Lee', N'36', N'January 1971', N'', N'', N'Thinking Spidey is causing harm to Gwendy, Iceman intervenes and tries to defeat old Webhead. Later, once misunderstandings are resolved, the two team up to take down a crooked politician.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3366, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Robbie Robertson; J. Jonah Jameson; Mary Jane Watson; Aunt May Parker; Anna May Watson; Harry Osborn; Arthur Stacy; Gwen Stacy; Herbert Knowles; Inspector Hargraves; Captain George Stacy (cameo flashback)', N'Stan Lee', N'36', N'April 1971', N'', N'', N'Peter heads for England looking for Gwendy but is forced to fight terrorists as Spider-Man. Realizing that Peter Parker can''t show up with Spidey already swinging around, Peter heads home, leaving Gwendy in mourning for her father.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3367, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Betty Brant; Robbie Robertson; Harry Osborn; Aunt May Parker; Anna May Watson; Mary Jane Watson; Green Goblin [Norman Osborn] (villain); Gwen Stacy (cameo flashback)', N'Stan Lee', N'36', N'May 1971', N'', N'', N'Peter returns from England and winds up getting a job with Norman Osborn. Unfortunately, Norman is shocked into becoming the Green Goblin again and sets his sights on Spidey.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3368, N'The Amazing Spider-Man', N'', N'Spider-Man [also appears as Peter Parker]; Harry Osborn; Mary Jane Watson; Green Goblin [Norman Osborn] (villain); Gwen Stacy (cameo flashback)', N'Stan Lee', N'36', N'June 1971', N'', N'', N'Spidey battles the Goblin while Harry starts popping pills to deal with the brush-off MJ has given him.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3369, N'The Amazing Spider-Man', N'', N'Spider-Man [also appears as Peter Parker]; Gwen Stacy; J. Jonah Jameson; Robbie Robertson; Harry Osborn; Green Goblin [Norman Osborn] (villain)', N'Stan Lee', N'36', N'July 1971', N'', N'', N'Spidey confronts the drug dealer who sold Harry the pills that put him in the hospital. Spidey and Goblin wage a pitched battle until the web-head confronts Norman with his sick son. Gwen returns from London.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3370, N'The Amazing Spider-Man', N'', N'Spider-Man [Peter Parker]; Gwen Stacy; Robbie Robertson; J. Jonah Jameson; Johnny Carson; Ed McMahon; Turpo (villain); Prisoners (villain)', N'Stan Lee', N'36', N'August 1971', N'', N'', N'Peter gets an assignment to snap some pics of a prison riot and swings in as Spidey to get the inside scoop. Once the situation is resolved, Spider-Man appears on the Tonight Show and gives a speech on the rights of prisoners.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3371, N'The Amazing Spider-Man', N'', N'Captain Marvel [Mar-Vell]; Professor Sneer', N'Marv Wolfman', N'36', N'November 1979', N'', N'', N'Captain Marvel prevents Professor Sneer from extinguishing the sun.', N'', N'Marvel', N'1570')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3372, N'The Amazing Spider-Man', N'', N'Peter Parker [Spider-Man]; Flash Thompson', N'Mike Lackey', N'36', N'Early January 1993', N'', N'', N'Peter and Flash spar in a boxing ring.', N'', N'Marvel', N'1570')
