INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3460, N'The Avengers', N'', N'the Avengers [Captain America [Steve Rogers]; Captain Marvel [Monica Rambeau]; Hawkeye; Henry Pym [also as the Ant-Man; Goliath; Yellowjacket in flashbacks]; Iron Man [Tony Stark]; the Scarlet Witch; She-Hulk; Thor [Donald Blake]; the Vision; the Wasp]; the Cobalt Man (flashback); the Masters of Evil [the Beetle [Abner Jenkins]; Egghead; Moonstone [Dr. Karla Sofen]; the Radioactive Man; the Shocker [Herman Schultz]; Tiger Shark]', N'Mark Gruenwald', N'36', N'February 1983', N'', N'', N'The Avengers attend Hank Pym''s trial for treason but Iron Man is missing. The Masters of Evil, secretly led by Egghead, "rescues" Pym during the trial in order to publicly disgrace him.', N'', N'Marvel', N'1571')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3461, N'The Avengers', N'', N'Avengers [Captain America [Steve Rogers]; Captain Marvel [Monica Rambeau]; Hawkeye; She-Hulk [also as Jennifer Walters]; Thor [Donald Blake]; Wasp]; Edwin Jarvis; Henry Pym; Masters of Evil [Beetle [Abner Jenkins]; Egghead (dies); Moonstone [Dr. Karla Sofen]; Radioactive Man; Shocker [Herman Schultz]; Tiger Shark] (villains)', N'Mark Gruenwald', N'36', N'March 1983', N'', N'', N'The Avengers learn from the Shocker that Egghead is behind the new Masters of Evil and Hank Pym''s "rescue". Pym has tricked Egghead into thinking he is working for him and is able to defeat all the Masters of Evil just as the Avengers arrive. Egghead is about to shoot Pym in the back when Hawkeye stops him by shooting the gun with an arrow. The gun explodes killing Egghead. Iron Man is still missing.', N'', N'Marvel', N'1571')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3462, N'The Avengers', N'', N'The Avengers [Captain America [Steve Rogers]; Hawkeye [Clint Barton]; Quicksilver [Pietro Maximoff]; Scarlet Witch [Wanda Maximoff]]; Kang the Conqueror; Princess Ravonna (Kang''s beloved, introduction)', N'Stan Lee', N'36', N'December 1965', N'', N'', N'Kang takes note that Steve Rogers is no longer an Avenger and easily defeats and captures the remaining three members. Cap comes to the rescue but is beaten too. Kang seems invincible!', N'', N'Marvel', N'1571')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3470, N'The Avengers', N'', N'The Avengers [Captain America [Steve Rogers]; Hawkeye [Clint Barton]; Quicksilver [Pietro Maximoff]; Scarlet Witch [Wanda Maximoff]]; Kang the Conquerer; Princess Ravonna; Baltag', N'Stan Lee', N'36', N'January 1966', N'', N'', N'The Avengers ally themselves with Princess Ravonna to stave off the invasion of Kang the Conquerer. Although Kang is victorious, his troops rebel against him and he finds he must partner with those he has just defeated - the Avengers and Princess Ravonna.', N'', N'Marvel', N'1571')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3463, N'The Avengers', N'', N'Avengers [Captain America [Steve Rogers]; Captain Marvel [Monica Rambeau]; Hawkeye; She-Hulk; Thor [Donald Blake]; Wasp]; Hank Pym; Captain Marvel [Mar-Vell] (flashback); Edwin Jarvis; Elysius; Eros of Titan; ISAAC; Masters of Evil [Beetle [Abner Jenkins]; Egghead (dead); Moonstone [Dr. Karla Sofen]; Radioactive Man; Tiger Shark]; Mentor; Plant-Man; Trish Starr', N'Mark Gruenwald', N'36', N'April 1983', N'', N'', N'Patricia Starr, Egghead''s niece, gives testimony on Hank Pym''s behalf. Pym''s trial ends and the prosecution withdraws all charges. In state court, Hawkeye is also found innocent in the death of Egghead. The Avengers also clear him of using unreasonable force under their by-laws. Pym and Janet van Dyne make peace. Hank retires as a superhero and gives the Yellowjacket apparel to Captain America. He leaves to work for a small research foundation in the midwest. Elysius, Mar-Vell''s former companion, releases Eros of Titan from his promise to take care of her.', N'', N'Marvel', N'1571')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3464, N'The Avengers', N'', N'the Avengers [the Beast [Hank McCoy] (flashback); Captain America [Steve Rogers]; Captain Marvel [Monica Rambeau]; Hawkeye [Clint Barton]; Hercules (flashback); the Scarlet Witch [Wanda Lehnsherr] (flashback); She-Hulk [Jennifer Walters]; Thor [also as Dr. Donald Blake]; the Vision (flashback); the Wasp [Janet Van Dyne]; Wonder Man [Simon Williams](flashback)]; Eros [becomes Starfox, an Avenger]; Iron Man [Jim Rhodes]; the Plant-Man; Tony Stark [Iron Man]', N'Mark Gruenwald', N'36', N'June 1983', N'', N'', N'Eros becomes Starfox and joins the Avengers.', N'', N'Marvel', N'1571')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3465, N'The Avengers', N'', N'Agatha Harkness (flashback); the Avengers [Captain America [Steve Rogers]; Captain Marvel [Monica Rambeau]; the Falcon (flashback); Hawkeye; Iron Man [Tony Stark] (flashback); Moondragon (flashback); Ms. Marvel [Carol Danvers] (flashback); Quicksilver (flashback); the Scarlet Witch; She-Hulk; Starfox; Thor [Donal Blake]; the Vision; the Wasp; Wonder Man (flashback)]; Bova (flashback); Crystal (flashback); Django Maximoff (flashback); Doctor Doom (flashback); Doctor Strange; Edwin Jarvis; the High Evolutionary (flashback); Immortus (flashback); Luna (flashback); Magda (flashback); Magneto (flashback); Sheila Danning; the Stranger (flashback); the Toad (flashback); the X-Men [Angel; the Beast; Cyclops; Iceman; Marvel Girl] (flashback)', N'Mark Gruenwald', N'36', N'August 1983', N'', N'', N'Origin of the Scarlet Witch retold in detail.', N'', N'Marvel', N'1571')
