INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3511, N'The Avengers', N'', N'The Avengers [Thor [Don Blake]; Giant-Man [Henry Pym]; The Wasp [Janet Van Dyne]; Iron Man [Tony Stark]]; Namor the Sub-Mariner; Hulk [Bruce Banner]; Fantastic Four [The Thing [Ben Grimm]; Mr. Fantastic [Reed Richards]; The Invisible Girl [Sue Storm]; The Human Torch [Johnny Storm]]; Spider-Man [Peter Parker]; The X-Men [Angel [Warren Worthington III]; The Beast [Hank McCoy]; Cyclops [Scott Summers]; Iceman [Bobby Drake]; Marvel Girl [Jean Grey]; Professor X [Charles Xavier]]; Rick Jones; The Teen Brigade; Jane Foster', N'Stan Lee', N'36', N'January 1964', N'', N'', N'The Avengers feel responsible for the danger the Hulk may cause after he left the team in the previous issue. When they find the green Goliath a battle ensues. The Sub-Mariner learns of the Hulk''s presence and after initially fighting each other Namor convinces the Hulk to join forces with him against the Avengers.', N'', N'Marvel', N'1571')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3512, N'The Avengers', N'', N'Edwin Jarvis; Captain America [Steve Rogers]; Quicksilver [Pietro Maximoff]; Goliath [Clint Barton]; Scarlet Witch [Wanda Maximoff]; Vision; Thor [Donald Blake]; Gloria Garsen', N'Rick Rutherford', N'68', N'February 1989', N'', N'', N'Marvel handbook-like profile on Edwin Jarvis.', N'', N'Marvel', N'1571')
