INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3516, N'The Avengers', N'', N'THe Avengers [Captain America [Steve Rogers]; Quasar [Wendell Vaughn]; Thor [Sigurd Jarlson]; She-Hulk; Namor the Sub-Mariner; Black Panther [T''Challa]; Gilgamesh]; Fantastic Four [Invisible Woman; Mr. Fantastic]; Avengers West [Wonder Man; Dr. Pym; Wasp; Vision; Tigra; Scarlet Witch]; Eternals [Sprite; Ikaris; Makkari; Thena; Phastos; Sersi]; Edwin Jarvis', N'David Wohl', N'36', N'October 1989', N'', N'', N'The Avengers race back to Avengers Island to tend to the badly injured Gilgamesh. Unable to help him, they contact the Eternal, Sersi, and, together, take Gilgamesh to Olympia, home of the Eternals.', N'', N'Marvel', N'1571')
