INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (3527, N'The Avengers', N'', N'The Avengers [Captain America [Steve Rogers]; Hercules; Sersi; Rage; Black Widow [Natasha Romanov]; Quasar [Wendell Vaughn]; Vision; Quicksilver]; Inhumans [Karnak; Timberius; Crystal; Lockjaw; Gorgon; Black Bolt; Medusa; Triton]; Thane Ector (introduction); Sybyl Dorn (introduction); the Fool (introduction); the Brethren (introduction); the Watcher; the Collector', N'Len Kaminski', N'36', N'July 1991', N'', N'', N'The Brethren crashland the Collector''s ship near the Blue Area of the Moon. Both the Inhumans and the Avengers send personnel to investigate. The Watcher Uatu is also interested in the coming of the Brethren and the Collector. The Brethren then transport themselves to Earth.', N'', N'Marvel', N'1571')
