INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (7782, N'Wolverine: Soultaker', N'', N'Wolverine; Yukio; Ashurado Ninjas (villain); Mana Yanowa (cameo flashback); Hana Yanowa (cameo flashback); Ryuki (cameo flashback)', N'Kenny Keil', N'36', N'May 2005', N'', N'75960605563000111', N'Wolvie is in Japan visiting Yukio when she shows him a necklace called the Mark of Mana which has been causing her to have terrible visions every since she touched it. Wolvie takes it to an old friend for identification who tells him that it is evil but is killed by ninjas before he can tell Wolvie why. Logan touches the necklace himself and begins to see visions of his own.', N'', N'Marvel', N'30473')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (7783, N'Wolverine: Soultaker', N'', N'Wolverine; Yukio; Mana Yanowa; Shosei Monks; Ashurado Ninjas (villain)', N'Kenny Keil', N'36', N'May 2005', N'', N'75960605563000211', N'Wolvie''s quest to discover the origins of a mysterious necklace brings him to a group of monks protecting a comatose girl named Mana. When ninjas attack, the monks hold them off while Wolvie rescues the girl. Placing the necklace named after her around her neck, Mana wakes up and tells him he has released an unspeakable evil on the world.', N'', N'Marvel', N'30473')
INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (7784, N'Wolverine: Soultaker', N'', N'Wolverine; Mana Yanowa (origin); Hana Yanowa (villain); Ryuki (villain); Shisengumi Zombies (cameo)', N'Jonathan Case', N'44', N'June 2005', N'', N'75960605563000311', N'Mana tells Logan her origin story and how the necklace he used to revive her was a holding cell for her evil twin sister and her demon ally. Wolvie is blessed by Mana as he prepares to face the evil he has unwittingly unleashed back into the world.', N'', N'Marvel', N'30473')
