INSERT [dbo].[issues] ([id], [series_title], [issue_title], [characters], [author_name], [page_count], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher], [series_id]) VALUES (7786, N'Wolverine: Soultaker', N'', N'Wolverine; Mana Yanowa; Yukio; Amiko; Hana Yanowa (villain); Ryuki (villain); Ashurado Demons (villain)', N'Jonathan Case', N'36', N'August 2005', N'', N'75960605563000511', N'Wolvie and Mana battle Hana and her demon allies but Logan is forced to give Hana the Sword of Blood to save the lives of Amiko and Yukio. Mana battles her sister while Wolvie uses his claws dipped in gold to cut her ties with the demon Ryuki. Mana kills Hana. After the battle is over, Amiko picks up the Sword of Blood revealing her to be of the line of Shoshi. Amiko agrees to stay with Mana and be trained as her successor.', N'', N'Marvel', N'30473')
