INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (54, N'', N'Tales from the Dark Multiverse: Crisis on Infinite Earths', N'February 2021', N'', N'76194137173300111', N'Tempus Fuginaut observes a Dark Multiverse where after the Crisis on Infinite Earths, the new single Earth that is formed is based on Earth 2, not Earth 1.  With their universe no longer in existence, the Justice League travel to Ragnarok to battle Surtur.  When they fall in battle, Surtur arrives on new Earth to face the combined might of the All-Star Squadron.  Surtur kills many of the team, and the surviving members retreat and regroup as the Justice Society of Earth to face Surtur in final battle.  But one of the team must make a personal sacrifice in order to ensure the Earth''s survival.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (55, N'', N'Guardians of the Galaxy', N'February 2021', N'', N'75960609657200911', N'Star-Lord undergoes a cosmic journey connected to his origins with the Master of the Sun. He returns to the world of the living just as Knull''s forces are attacking the Guardians.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (56, N'', N'The Orville', N'December 2020', N'', N'76156800678000411', N'With the Orville officially being ordered not to interfere, Talla assumes the role of legendary Okudum heroine Xandia in an attempt to stop the Nazh from enslaving the Okudum.', N'', N'Dark Horse')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (57, N'', N'Batman / Catwoman', N'February 2021', N'', N'76194136737800111', N'In the present, Andrea Beaumont shows up in Gotham asking for Bruce''s help in tracking down her missing son, leading to Batman and Catwoman teaming up to track down the boy.  In the future, an elder widowed Selina visits an old friend to reminisce about old times.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (58, N'', N'Justice League: Endless Winter', N'February 2021', N'', N'76194137202000111', N'After battling Icicle, Catman, Rampage, and Multiplex, the Justice League is called to the Arctic Circle where a Stagg Industries excavation inadvertently frees the Frost King, who has been trapped in the ice for centuries and now plans his revenge against the world.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (59, N'', N'Batman: The Adventures Continue', N'February 2021', N'', N'76194136778100711', N'Red Hood puts Robin in a deathtrap and gives Batman the chance to save him - if the Caped Crusader kills the Joker.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (60, N'', N'Justice League by Scott Snyder The Deluxe Edition', N'2020 [February 2021]', N'', N'978177950584253999', N'Returning to present day Earth, the League learns from Mera and Starman of Luthor''s efforts to influence people to reject justice and embrace doom which has resulted in violence breaking out around the world.  The League realizes they will need to recruit an army of heroes to help them stop Luthor''s plan and save the Multiverse.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (61, N'', N'Tales from the Dark Multiverse: Wonder Woman: War of the Gods', N'February 2021', N'', N'76194137172600111', N'Tempus Fuginaut observes a Dark Multiverse world where after Wonder Woman leads the heroes to victory in the War of the Gods, the witch Hecate kills Steve Trevor, Etta Candy, and Hippolyta, and uses Wonder Woman''s rage for Hecate to take over her body.  With Hecate possessing Wonder Woman''s body, she plans on conquering the world.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (62, N'', N'Metal Men', N'February 2021', N'', N'76194135874101211', N'Sideways helps Magnus and the Metal Men escape from the Dark Multiverse just before it is destroyed. Back home, the robots decide to go their own ways for a time, except for Tina, who wants to stay so that she and Doc can get to know each other.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (63, N'', N'DCeased: Dead Planet', N'February 2021', N'', N'76194136565700611', N'As the heroes gather to determine how to use Cyborg''s blood to cure those inflicted with the anti-life virus, Professor Ivo launches an army of Amazos to kill those infected before they can be cured.  Meanwhile, Zatanna discovers that Constantine is working behind the scenes using questionable methods to help the heroes.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (64, N'', N'Batman', N'Early February 2021', N'', N'76194134182810411', N'While Bruce, Harley, and Clownhunter are held captive by Ghost-Maker, Dick reveals to Barbara the secret history between Bruce and Ghost-Maker.', N'', N'DC')
