INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (72, N'', N'Action Comics', N'January 2021', N'', N'76194134388402711', N'Having defeated the extra-dimensional Parasite, Superman, Supergirl, both Superboys, and Brainiac 5 have a final showdown with Red Cloud.  Meanwhile, confusion reigns at the FBI''s investigation of the Daily Planet, resulting in Lois being taken into custody by Agent Chase.', N'', N'DC')
