INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (74, N'', N'Dark Nights: Death Metal The Multiverse Who Laughs', N'January 2021', N'', N'76194137000200111', N'On a Dark Multiverse Earth that has been overwhelmed by Scarecrow''s fear toxin for 2 years, Steel saves Jean-Paul Valley from being overcome by the toxin and inspires him to become a hero.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (75, N'', N'True Believers: King in Black - Iron Man/Doctor Doom', N'January 2021', N'', N'75960620033700111', N'Doctor Doom found a way to travel through time with the time cube. In search of mystical secrets from the past, he ends up unleashing an incident with Stark International. Iron Man and Doctor Doom are sent into the past by Gert Hauptmann, a Doom scientist who hated him for murdering his brother. Doom is in his castle in Latveria, but at that time the leader of Latveria was Zorba, his rival.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (76, N'', N'Aquaman', N'January 2021', N'', N'76194134183506511', N'With his friends and allies by his side, Aquaman defeats Ocean Master and Mera is freed from Lernaea''s clutches.  Mera follows through on her promise and begins the work to transform Atlantis from a monarchy to a democracy that will work for all Atlanteans, and she welcomes Orm''s followers from Dagon to join them.  Aquaman and Mera are then reunited with Princess Andy, but when a new threat seems to arise, it turns out to be a ruse created by Aquaman to bring in all their friends to be there to surprise Mera with an impromptu wedding.', N'', N'DC')
