INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (84, N'', N'Punchline', N'January 2021', N'', N'76194137133700111', N'As Punchline faces trial after the events of the Joker War, she pleads innocent claiming she was manipulated by the Joker.  Harper Row becomes concerned when her brother Cullen becomes addicted to a series of podcasts recorded by Punchline which portrays her actions and those of the Joker in a sympathetic light, which helps to swing public opinion in her favor.', N'', N'DC')
