INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (155, N'Mu, the Lost Continent', N'Corto Maltese', N'July 2020', N'', N'978168405691053499', N'Corto Maltese is along the shores of Central America conducting a search for a lost continent. A premonition hovers over the entire dreamlike story, as if fate had decided in advance that this would be Corto Maltese''s last adventure. The feeling is underscored by the return of many of the major characters seen throughout the long-running saga.', N'', N'IDW')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (156, N'', N'Wonder Woman', N'Early October 2020', N'', N'76194134285676011', N'After Maxwell Lord uses his mental powers to stop a prison riot, Wonder Woman is suspicious of his motives.  Diana learns of cases of people who have been hallucinating and taking actions that put others in jeopardy.  After stopping a hallucinating construction crane operator from demolishing an occupied building, Diana is soon confronted by Parademons.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (157, N'', N'Superman', N'October 2020', N'', N'76194135744702411', N'Superman and Doctor Fate team up to battle Xanadoth, an ancient Lord of Chaos who is out to steal the helmet of Nabu.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (158, N'', N'Justice League Odyssey', N'October 2020', N'', N'76194135726302311', N'While Darkseid and the Eskaton battle, Epoch and Arla Hax try to complete the Revision Mechanism to re-set time.  After Darkseid defeats the Eskaton, he is left weakened and sends Cyborg to command his troops to claim the Revision Mechanism for Darkseid.  After Cyborg defeats the other heroes, only Jessica is left to stop him.', N'', N'DC')
