INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (200, N'', N'The Terrifics', N'May 2020', N'', N'76194135189602611', N'When Simon Stagg learns that he has pancreatic cancer, he uses the opening run of Gateway City''s new rapid transit holo-pod system as a cover for something sinister that could save his life. But the other passengers on the system vanish too, including Plastic Man''s son Luke. The Terrifics track down Stagg far underground, where they are attacked by monsters, but the surprise arrival of the T-Council may work in their favor.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (201, N'', N'Ant-Man', N'May 2020', N'', N'75960609705000311', N'Scott goes to the Avengers for help against Macrothrax but they send him to see a former member of the Heroes for Hire, Black Cat. Spider-Man convinces her to help, then tags along, not wanting to leave Felicia alone with Scott. Ant-Man and Stinger learn that they have to travel to the Savage Land to stop Macrothrax and his avatars.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (202, N'', N'True Believers: Empyre - Anelle', N'May 2020', N'', N'75960609880400111', N'The FF travel to the Skrull homeworld to avenge the death of Franklin Storm.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (203, N'', N'The Terrifics', N'April 2020', N'', N'76194135189602511', N'When Gateway City is threatened with destruction, the Terrifics find themselves trapped in a series of possible outcomes before Mr. Terrific invents a way out of the situation.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (204, N'', N'Swamp Thing: Tales from the Bayou', N'[April] 2020', N'', N'978177950115851699', N'The Barren has gathered the powers of all the realms including the Green, and he plans to use them to wipe out all existence unless Swamp Thing can stop him.  Meanwhile in Hell, Briar meets her father who makes her an offer that comes with a price.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (205, N'', N'Archie: The Married Life - 10th Anniversary', N'March 2020', N'', N'76281686510400611', N'As everyone converges on the hospital to support Veronica, new couples are forming: Cheryl and Reggie, and Veronica and Moose. Kevin delivers the news that Mr. Lodge will probably recover. Archie suggests that they revive The Archies, this time with an expanded roster to include Midge, Cheryl, Moose, and Kevin. Later, Dilton arrives and takes Archie, Betty, and Veronica on a ride in his car.', N'', N'Archie')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (206, N'', N'Justice League', N'Early April 2020', N'', N'76194135724904011', N'Sodam Yat of Daxam arrives on Earth to warn the League of an imminent threat: The Eradicator has created a new breed of Daxamite soldiers who he will use to lay waste to Earth and remake it as a Kryptonian colony.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (207, N'', N'Iron Man #149 [Marvel Legends Reprint]', N'2002', N'', N'', N'Doctor Doom found a way to travel through time with the time cube. In search of mystical secrets from the past, he ends up unleashing an incident with Stark International. Iron Man and Doctor Doom are sent into the past by Gert Hauptmann, a Doom scientist who hated him for murdering his brother. Doom is in his castle in Latveria, but at that time the leader of Latveria was Zorba, his rival.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (208, N'', N'Archie: The Married Life - 10th Anniversary', N'February 2020', N'', N'76281686510400511', N'Archie decides not to sign the big record deal and goes home to Betty, who wants him to join her in her new catering business. Mr. Lodge has a heart attack and as Veronica''s friends converge on the hospital to support her she sees Moose in a new light. Reggie and Cheryl grow closer.', N'', N'Archie')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (209, N'', N'Dial H for Hero', N'March 2020', N'', N'76194135848201011', N'Miguel and Summer have been traveling through the Multiverse on the Operator''s mission when they land on a chaotic world of heroes that are dual combinations of heroes they know, including themselves. Mister Thunderbolt finds them there and reveals that both he and the Operator are Robby Reed. Feeling betrayed by the Operator, Miguel is tempted by Mister Thunderbolt to use one of the other dials, which transforms him into four different versions of Supermiguel.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (210, N'', N'The Terrifics', N'March 2020', N'', N'76194135189602311', N'Bizarro''s time machine has trapped the Terrifics in a loop where they experience the same few minutes over and over. Mr. and Ms. Terrific think they have figured a way out, but it''s dangerous.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (211, N'', N'Justice League Dark', N'March 2020', N'', N'76194135725601811', N'Trapped in the Collective Unconscious, Wonder Woman battles the Upside-Down Man.  Meanwhile, at the Hall of Justice, Circe, in Wonder Woman''s body, and the Injustice League Dark revive the dragon Drakul Karfang, while Zatanna, Constantine, Kent, and Khalid try to keep the Black Diamond containing Eclipso away from Circe.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (212, N'', N'Batman: Last Knight on Earth', N'February 2020', N'', N'76194135390600311', N'In the aftermath of Omega''s defeat, Batman''s allies resume their costumed identities.  A rocket from another time and dimension arrives on Earth with a baby Kal-El, who is found by Batman, Wonder Woman, and the Joker.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (213, N'', N'Wonder Woman', N'Late February 2020', N'', N'76194134285608311', N'Wonder Woman fights the Cheetah on Themyscira to protect the Amazons.  When the battle shifts to Boston, the Silencer joins the battle attempting to kill the Cheetah.  Diana must stop the Cheetah while simultaneously protecting her from the Silencer.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (214, N'', N'Batman / Superman', N'February 2020', N'', N'76194136270000511', N'Batman and Superman battle the infected heroes to stop them from bringing a satellite from the Dark multiverse that the Batman Who Laughs plans to use to infect everyone on Earth.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (215, N'', N'Shazam!', N'January 2020', N'', N'76194136016400811', N'Pedro, Eugene, and the Wizard Shazam seek to escape from the Wozenderlands to find their way home.  While searching for his siblings, Billy finds himself trapped in the Darklands and confronted by the ghost of a past Shazam champion.  On Earth, Sivana and Mr. Mind seek to gain power over Billy by sending Black Adam and the Seven Deadly Sins to capture his father.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (216, N'', N'Justice League Dark', N'January 2020', N'', N'76194135725601711', N'Wonder Woman returns from Circe''s realm, but the other heroes soon discover that she is really Circe, using Hecate''s power to possess Wonder Woman''s body.  As Circe tries to control the power of Eclipso through the Black Diamond, the Justice League Dark fights back, only to be faced by Circe''s Injustice League Dark.  Meanwhile in Circe''s realm, Wonder Woman''s soul self tries to find a way home.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (217, N'', N'The Terrifics', N'January 2020', N'', N'76194135189602211', N'Bizarro returns to Bgztl to steal more Phantom Zone crystal to power Mr. Terrible''s time machine, which now turns the Terrifics into children.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (218, N'', N'The Infected: Scarab', N'[January] 2020', N'', N'76194136646300111', N'Infected by The Batman Who Laughs, Jaime finds himself changing into Scarab, an evil version of Blue Beetle.  Brenda and Paco try to help Jaime, but when attacked by Ghostfire, Scarab''s dark side takes control.', N'', N'DC')
