INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (220, N'', N'Dial H for Hero', N'January 2020', N'', N'76194135848200911', N'Miguel and Summer are not doing well as Daily Planet interns, especially when Miguel is distracted after meeting a cute boy who recognizes him as Supermiguel. The Operator calls to enlist them for another mission, to locate another one of the dials, but the Thunderbolt Club is also looking for it.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (221, N'', N'Wonder Woman', N'Early January 2020', N'', N'76194134285608211', N'Freed by the Dark Fates, the Cheetah attacks the Amazons hoping to draw out their patron gods in order to kill them.  Wonder Woman seeks out help before returning to Themyscira to confront the Cheetah.', N'', N'DC')
