INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (223, N'', N'Archie', N'December 2019', N'', N'76281646747670811', N'Archie and Sabrina are relaxing in Fox Forest, when suddenly Sabrina insists that they have to leave. Elsewhere in Riverdale, Archie''s friends are getting tangled up with the same forest in different ways.', N'', N'Archie')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (224, N'', N'The Orville', N'October 2019', N'', N'76156800498400411', N'Trapped in Krill space, the Orville hides in an asteroid field while they develop a plan to escape the Krill ships that are hunting for them.', N'', N'Dark Horse')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (225, N'', N'Wonder Woman', N'Early December 2019', N'', N'76194134285608011', N'As Diana grieves over her break-up with Steve, she pursues the Cheetah, armed with a serum from Veronica Cale that can break Cheetah''s bond with the God Killer sword.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (226, N'', N'Detective Comics', N'Early December 2019', N'', N'76194134373001311', N'Batman tracks down Mister Freeze''s lab to rescue the kidnapped women, but Freeze escapes when he turns his failed frozen experiment victims against Batman.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (227, N'', N'Blossoms 666', N'October 2019', N'', N'978168255797651799', N'Veronica learns that she is still a vampire, and that her parents are vampires.', N'', N'Archie')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (228, N'', N'DCeased', N'December 2019', N'', N'76194135976200511', N'After the destruction of Washington D.C., Baltimore, and Metropolis, the heroes gather the world''s survivors to seek sanctuary on Themyscira and in Gotham City.  As the heroes gather at the Fortress of Solitude to discuss evacuating the planet, they face an attack by one of the world''s most powerful heroes who has been transformed into a zombie.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (229, N'', N'Dial H for Hero', N'October 2019', N'', N'76194135848200611', N'Chaos breaks out in Metropolis as everyone becomes a superhero. Miguel leaves the Heroverse for Metropolis, where he saves a plane from crashing. Mister Thunderbolt gloats about his plans to steal three more H-Dials.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (230, N'', N'Wonder Woman', N'Late October 2019', N'', N'76194134285607711', N'Aphrodite is found dead at Diana''s home, a victim of the Cheetah.  After a memorial service is held, Diana seeks justice by confronting the Cheetah, but she is unaware that the death of the Goddess of Love has affected her powers.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (231, N'', N'The Terrifics', N'October 2019', N'', N'76194135189601911', N'Luthor has given Bizarro a time machine, with which Bizarro tries to reverse technological progress on Htrae. He''s not successful at it, so he enlists his enemy, Mr. Terrible, to travel with him to Earth to do the same thing.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (232, N'The Golden House of Samarkand', N'Corto Maltese', N'Paco Zarco', N'', N'156.000', N'After the Fable of Venice"', N'', N'IDW')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (233, N'', N'Justice League', N'Late October 2019', N'', N'76194135724903011', N'Starman is able to see a Hypertime vision of three days in the future that shows Lex Luthor and Perpetua triumphant in defeating the Justice League and beinging about the age of Doom.  The League recruits all of Earth''s heroes to join the battle against Doom.  Meanwhile, Luthor has recruited all the world''s villains, enhanced by his offers, to join the Legion of Doom.  Starman''s plan involves splitting the core League into two teams, one to travel to the future, the other to travel to the past, both to retrieve pieces of the Totality from those eras that they can use to help stop Luthor.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (234, N'', N'Death''s Head', N'October 2019', N'', N'75960609353300211', N'Avengers (except Captain America) are listening to digital music from Joywave while thwarting evil, last panel shows Captain America listening to Joywave vinyl at home.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (235, N'', N'The Orville', N'August 2019', N'', N'76156800498400211', N'Ed and Gordon learn that the Chog are dependent upon the wreck of the Burton for food, but they are unaware of the automated defense system that fires on any ship entering orbit.  Ed and Gordon must find a way to warn the Orville before it arrives searching for them, and also find a way to save the Chog.  Meanwhile on the Orville, Kelly considers a relationship with Cassius.', N'', N'Dark Horse')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (236, N'', N'Wonder Woman', N'Early October 2019', N'', N'76194134285607611', N'After re-uniting with her mother, Diana returns to Earth to re-unite with Steve Trevor, and then meets with Veronica Cale to bring her back to Themyscira to see her daughter.  Meanwhile, The Cheetah, now empowered with the God Killer sword, breaks into Wonder Woman''s home but finds only Aphrodite there.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (237, N'', N'The Terrifics', N'September 2019', N'', N'76194135189601811', N'Michael convinces the Noosphere to leave Earth, but Paula thinks it should have been destroyed. Meanwhile, Luthor enlists Bizarro in his master plan.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (238, N'', N'Wonder Woman', N'Late September 2019', N'', N'76194134285607511', N'Diana, Maggie, and Atlantiades must join forces with Antiope and a group of Amazon refugees that had escaped to Dimension Chi to return to Themyscira where Grail has taken control of the remaining Amazons and holds Hippolyta captive.  Back on Earth, Lex Luthor offers Cheetah a weapon capable of killing gods to use to destroy all Amazons.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (239, N'', N'Dial H for Hero', N'September 2019', N'', N'76194135848200511', N'The Operator saves Miguel from Mister Thunderbolt, but the villain now has the H-Dial. Meanwhile, Summer takes the Supermobile from Snapper''s museum and flies to Metropolis, arriving just as Mister Thunderbolt uses the stolen H-Dial to grant superpowers to everyone in the city.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (240, N'', N'Out of the Blue', N'July 2019', N'', N'978194902816451999', N'In a dogfight over Norway Broome is killed; the T for Bitch is shot down but Jamie and Joseph survive.', N'', N'AfterShock')
