INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (244, N'', N'Wonder Woman', N'Early September 2019', N'', N'76194134285607411', N'In Dimension Chi, Wonder Woman, Maggie, and Atlantiades are confronted by an enraged Empress Hippolyta, who attacks the trio.', N'', N'DC')
