INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (246, N'', N'DCeased', N'August 2019', N'', N'76194135976200211', N'As the virus continues to spread around the world, Aquaman encounters zombies on a freighter.  Superman tries to protect Lois, Jon, and Damian in Metropolis while Batman is missing.  A camping trip with Hal, Ollie, and Dinah goes bad when Hal becomes infected.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (247, N'', N'Shazam!', N'August 2019', N'', N'76194136016400611', N'Billy and Mary escape from King Kid in the Funlands and suddenly find themselves back home on Earth, where Billy meets his long lost father and Mary must make a decision.  Meanwhile, Sivana and Mr. Mind try to convince Black Adam to join forces with them, and in the Gamelands, Pedro and Eugene try to get a high score so they can return home but they encounter an unexpected ally.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (248, N'', N'Thanos', N'July 2019', N'', N'75960609340300211', N'The Butcher Squadron is intrigued when Thanos takes Gamora prisoner rather than killing her.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (249, N'', N'Blossoms: 666', N'July 2019', N'', N'76281628422600411', N'Cheryl and Jason declare a truce to combine forces and deal with Julian.  Julian tries to corrupt Betty.', N'', N'Archie')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (250, N'', N'Dial H for Hero', N'July 2019', N'', N'76194135848200311', N'Officer Grandle uses the H-Dial and confronts Miguel and Summer. Summer''s past provides the inspiration when she uses the Dial to fight back. The Operator reveals himself to be Robby Reed.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (251, N'', N'The Terrifics', N'July 2019', N'', N'76194135189601611', N'The Terrifics must battle their way through a host of virtual menaces based on the twelve plagues of Egypt, and find that the situation is controlled by a virtual version of Simon Stagg.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (252, N'', N'Dial H for Hero', N'June 2019', N'', N'76194135848200211', N'Miguel and Summer argue about whether to go home but when the H-Dial transforms a member of the Thunderbolt Club into a menace that Miguel has to fight, they realize that they need to reach Metropolis where they can get Superman''s help.', N'', N'DC')
