INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (256, N'', N'True Believers: Avengers - Ronin', N'June 2019', N'', N'75960609424000111', N'In the past, The Mighty Avengers have Brother Voodoo use his magic to determine if the New Avengers are holed up in Dr. Strange''s sanctum, but Stephen''s magic is too strong and the team stays hidden. Clint Barton shows up at the sanctum and joins the New Avengers as Ronin. Dr. Strange casts a spell to determine the individual intentions of the New Avengers and is happy to find that all are loyal to the cause. The New Avengers head to Japan to rescue Maya. In the present, The New Avengers battle Elektra and the Hand. Maya, who has come under the Hand''s control, stabs Dr. Strange.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (257, N'Power', N'Animosity', N'February 2019', N'', N'978194902803451499', N'Jesse reveals the secret of the Headmistress to her followers.', N'', N'AfterShock')
