INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (261, N'The Secret Rose', N'Corto Maltese', N'February 2019', N'', N'978168405402251999', N'When Corto Maltese accompanies his old friend Professor Steiner to pay a visit to the writer Hermann Hesse, he becomes immersed in a shifting, dreamlike adventure involving the quest for the Holy Grail, the Devil, the Sandman, and Death, as well as the knight Klingsor (a character from the epic poem Parzifal, Wagners opera Parsifal, and a novel by Hesse).', N'', N'IDW')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (262, N'', N'Shazam!', N'April 2019', N'', N'76194136016400311', N'In the Funlands Billy and his foster siblings meet King Kid, the ruler of the Funlands, who wants to join them as the seventh member of their family and to share in their power.  When Mary decides that they need to return home, King Kid''s true colors are revealed.', N'', N'DC')
