INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (264, N'', N'Gen 13', N'1998', N'', N'', N'Fairchild is brought into the Gen 13 program, introduced to her fellow gen actives and develops powers.', N'', N'Image')
