INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (266, N'', N'Archie Meets Batman ''66', N'March 2019', N'', N'76281654852600611', N'Archie and his friends assume their superhero identities to help Robin and Batgirl free Batman from the villains that captured him. Dilton manages to reverse the mind control ray that enslaved Riverdale''s adults, and everyone dances at Pop''s in the end.', N'', N'Archie')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (267, N'', N'The Green Lantern', N'March 2019', N'', N'76194135745400311', N'Volgar Zo has shrunk and captured the Earth and is auctioning it to the highest bidder when Hal and the Corps arrive.  Hal has to deal with The Shepherd, who won the Earth in the auction and has brainwashed the population to accept him as their god, while the rest of the Corps go after the intergalactic villains who had gathered for the auction.', N'', N'DC')
