INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (271, N'', N'Shazam!', N'February 2019', N'', N'76194136016400111', N'In the past, Mary runs away from cruel foster parents and is placed in foster care with the Vasquez family.  Freddy helps Mary free a rabbit from becoming a lab animal and she takes it as her pet.  Later, when Mary and Darla change into their magical selves, Hoppy the bunny is affected by the magic lightning and gains powers.', N'', N'DC')
