INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (445, N'In Siberia', N'Corto Maltese', N'February 2017', N'', N'978163140848952999', N'Corto, Rasputin and Shanghai Lil meet the Mongolian General Sukhe Bator in his headquarters. They combine forces to attack General Chang and his armoured train. Chang survives and Corto finds him in Hong Kong, Chang dies after drinking poisoned tea. In April 1920, Corto has a final meeting with Shanghai Lil.', N'', N'IDW')
