INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (467, N'', N'Shazam!', N'July 2019', N'', N'76194136016400511', N'In the Funlands, Billy and Mary try to convince King Kid to free his adult prisoners.  In the Gamelands, Eugene schemes to use his gaming skills to win points so that he and Pedro can return home, but he runs into an unexpected obstacle.  In the Wildlands, Freddy and Darla go on trial for being human and are sentenced to death by tiger.  Meanwhile, Black Adam searches for Billy to stop the power of the Magiclands from being unleashed on Earth.', N'', N'DC')
