INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (781, N'', N'Essex County', N'March 2007', N'', N'978189183088450995', N'Lester is a 10 year old orphan who lives with his uncle on a isolated farm in Essex County.  He finds a friend in Jimmy Lebeuf, a former hockey star who was injured in his first and only major league game.  The two escape their lonely lives fighting alien invaders and playing pond hockey.', N'', N'Top Shelf')
