INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (804, N'', N'Ultimate Spider-Man', N'April 2011', N'', N'75960607496915311', N'While Iron Man begins Spider-Man''s S.H.I.E.L.D.-mandated superhero training, Mysterio tries to convince Black Cat to give him the Zodiac Key.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (805, N'', N'Notes from a Defeatist', N'2003', N'', N'978156097510651995', N'A Brueghel-inspired tale about a Cooper''s daughter who avoids being burned at the stake when people in the village start defecating riches instead of stool.', N'', N'Fantagraphics')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (806, N'', N'Ultimate Spider-Man', N'March 2011', N'', N'75960607496915211', N'Peter and Gwen sort out their relationship after Iron Man visits the house for Spider-Man''s superhero training.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (807, N'', N'The Uncanny X-Men', N'March 2011', N'', N'75960602461253211', N'Utopia remains under quarantine as the HX-N1 influenza continues to spread throughout the island''s mutant population.  In Chinatown, Angel''s team confronts the Collective Man attracting the attention of the Sublime Corporation''s manufactured version of the original X-Men.  Meanwhile, Emma Frost, Fantomex and Shadowcat must deal with a very angry Sebastian Shaw.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (808, N'', N'Action Comics', N'March 2011', N'', N'76194120001989711', N'Spalding discovers information that the original articles on the black spheres were planted, but can''t yet tell Luthor that as Luthor is about to interview Joker. At the interview they banter back and forth a while, but Joker reveals he has one of the spheres, and that it''s a keyhole. Spalding activates it, enlarging it. Luthor leaves the room, and RoboLois pushes Spalding into the sphere, and it shrinks back to small size. When Luthor asks what happened, RoboLois blames Joker.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (809, N'', N'Wolverine and Jubilee', N'March 2011', N'', N'75960607429700111', N'Jubilee, pouting heavily about being turned into a vampire, runs away from Utopia to have a good time in San Francisco. Wolvie and Rockslide pursue and find her in a cargo container full of dead bodies.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (810, N'', N'Lady Mechanika', N'October 2010', N'', N'753182726816', N'A brief history of how Lady Mechanika came to be written, along with early sketches of the concept.', N'', N'Aspen')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (811, N'Maggie, Hopey & Ray', N'Locas', N'June 2009', N'', N'978160699156553999', N'Ray remembers a heated teenage debate on the merits of rap music.', N'', N'Fantagraphics')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (812, N'', N'Palestine: The Special Edition', N'[October] 2007', N'', N'978156097844290000', N'Sacco recalls his acquaintance with a religious Oxford student in Jerusalem as well as some conversations he had with others concerning the peace process. The last sequence describes the time he saw some Israeli soldiers make a boy stand out in the pouring rain while they questioned him. This for him sums up the possible future outcomes for peace in the area.', N'', N'Fantagraphics')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (813, N'Maggie the Mechanic', N'Love and Rockets Library', N'January 2007', N'', N'978156097784151495', N'With Bomber in tow and back on the run from Costigan, Penny dyes her hair to disguise herself from his spies. Once she finds out that Bomber has a wife, she goes back to Costigan. When Costigan''s ex-wife turns up to make a scene, she flees yet again.', N'', N'Fantagraphics')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (814, N'', N'Wolverine: The Best There Is', N'March 2011', N'', N'75960606852400211', N'Beast and Logan discuss his infection (by Corruptor''s gland). Wolverine is attacked by Sikes, Madcap, meets Mortigan Goth and a boy with his head in a bag (Flip, Winsor''s son), is attacked by Vic Slaughter and Chris Daniels.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (815, N'', N'Action Comics', N'February 2011', N'', N'76194120001989611', N'Lois has volunteered Jimmy to be part of a charity date auction. Also participating is Chloe. Mallory wins a date with her, while nobody wants to bid for Jimmy. That is, until Maggie shows up with a $1,000,000 bid. They enjoy some time together, until Maggie suddenly shows magical powers, and reveals her last name is Mxyzptlk. Jimmy freaks out and tries to hide, but suddenly finds himself marrying her.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (816, N'', N'Ultimate Spider-Man', N'February 2011', N'', N'75960607496915111', N'A month ago the Black Cat was determined to kill Kingpin, when she witnesses Mysterio murdered him and threw it out the window, Iron Man passes for Peter to take his first super hero training day.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (817, N'', N'The Uncanny X-Men', N'February 2011', N'', N'75960602461253111', N'Utopia is in grips of the HX-N1 influenza pandemic forcing Cyclops to impose a quarantine on the island. Dr. Rao attempts to confirm that this mutant strain of flu is harmless to humans while Angel''s X-Men investigate a robbery on the mainland that was violently stopped by a team of X-Men imposters. Elsewhere, Emma Frost explains her complicated relationship with Sebastian Shaw to her accomplices in his kidnapping.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (818, N'', N'Batman: A Word to the Wise', N'[Summer] 1992', N'', N'', N'The Joker and thugs comes to Canada and starts stealing rare geography books across the country,Batman confronts him at the Canadian National Exhibition but Joker escapes. Batman enlists the help of some local teens,the trio travel to Edmonton via the Batplane then visit a Zellars for clothes before tracking Joker to the Calgary Stampede where Joker declared to the audience that he owned all of north america from a rare land grant hidden inside one of the rare books. Batman thwarts Joker''s plan stating the land grant expired the previous day then proceeds to apprehend Joker.', N'', N'DC')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (819, N'', N'Second Wave', N'December 2007', N'', N'978193450606651499', N'The Martians attack the earth and end up in Miles and Gina''s neighborhood. Miles panics and makes Gina wait in the basement while he tries to pull the car around to the door. A Martian blocks Miles'' car and steps all over his house. The army attacks the Martian standing over Miles'' house and Gina is killed in the fallout. Miles vows vengeance and it appears he may get the opportunity as the second wave of Martians lands.', N'', N'Boom! Studios')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (820, N'', N'What If? Wolverine: Father', N'February 2011', N'', N'75960607338200111', N'On earth 615.9, Venompool is deserted by the Beyonder who grows tired of living their homeless-chic lifestyle. VP decides it is time for him to return to relevance as well and sells his other drinking buddy, Tony Stark, to AIM to fund his venture. After ridding himself of the evil jheri curl that the Beyonder saddled him with, he tries to become a famous hero but finds the work hard and unrewarding. Police officer Fury tells him there is only one way to gain true status in the heroic world--join the Secret Wars.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (821, N'Too Far Gone', N'The Walking Dead', N'2010', N'', N'978160706329251499', N'During a funeral, the community is attacked by an outside group.', N'', N'Image')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (822, N'', N'The Uncanny X-Men', N'January 2011', N'', N'75960602461253011', N'Utopia is in the early stages of an influenza pandemic.  The HX-N1 flu appears limited to mutants, so Cyclops imposes a quarantine on the island to prevent both the spread of the strain amongst mutants and panic amongst humans.  To further calm fears, Angel forms a temporary X-Men team in San Fransisco with a membership that includes Dazzler, Northstar, Pixie and Storm.', N'', N'Marvel')
INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (823, N'', N'Action Comics', N'January 2011', N'', N'76194120001989511', N'Jimmy starts his (and Chloe''s) plan to make Metropolis look boring, so that the Dalwythian celebration will not destroy Earth. Perry reads from his novel, Supergirl shops at Yarn Barn, Martha Kent describes Smallville, and the bars and clubs are all closed. It works; the aliens leave.', N'', N'DC')
