INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (853, N'', N'13 Chambers', N'September 2008', N'', N'70985300676300111', N'By presidential decree, Marshal 13 is tasked with contacting his 12 fellow marshals, retrieving their unique 13 chambered guns and returning them to Washington D.C. as Andrew Johnson has discontinued their service. Things go smoothly until 13 reaches Four Corners to discover that ghoulish looking mining magnate, Bartholomew York, has killed Marshal 11 and taken his gun. Marshal 13 has to make it through York''s men, retrieve the weapon, apprehend the villain and save the father of the lovely Belle in the process.', N'', N'Image')
