INSERT [dbo].[issues] ([id], [issue_title], [series_title], [release_date], [ISBN], [UPC], [summary], [cover_link], [publisher]) VALUES (856, N'', N'The Uncanny X-Men', N'July 2010', N'', N'75960602461252411', N'Nightcrawler has sacrificed his life to get Hope to Utopia and the X-Men begin to mourn the tragic loss. Hope finally meets the X-Men, including Cable''s father Cyclops. Donald Pierce contacts Bastion from inside Utopia and they begin to put the next phase of their master plan into action. Cyclops sends the X-Club to investigate Bastion''s towers. Nightcrawler''s funeral is held and, unable to contain his anger and grief any longer, Wolverine storms off with X-Force.', N'', N'Marvel')
